mapper.select:
/* com.innobiz.orange.web.ap.dao.ApOngoFormItemDDao.selectApOngoFormItemD */
SELECT

	T.FORM_ID,
	T.FORM_SEQ,
	T.FORM_COMB_SEQ,
	T.ROW_CNT,
	T.COL_CNT,

FROM AP_ONGO_FORM_ITEM_D T

	AND T.FORM_ID = #{formId, jdbcType=VARCHAR}
	AND T.FORM_SEQ = #{formSeq, jdbcType=INTEGER}
	AND T.FORM_COMB_SEQ = #{formCombSeq, jdbcType=INTEGER}
	${whereSqllet}

ORDER BY ${orderBy}
ORDER BY T.FORM_ID, T.FORM_SEQ, T.FORM_COMB_SEQ

LIMIT #{pageStrt, jdbcType=NUMERIC}, #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.ap.dao.ApOngoFormItemDDao.countApOngoFormItemD */
SELECT COUNT(*) CNT
FROM AP_ONGO_FORM_ITEM_D T

	AND T.FORM_ID = #{formId, jdbcType=VARCHAR}
	AND T.FORM_SEQ = #{formSeq, jdbcType=INTEGER}
	AND T.FORM_COMB_SEQ = #{formCombSeq, jdbcType=INTEGER}
	${whereSqllet}

