mapper.select:
/* com.innobiz.orange.web.ap.dao.ApOngoFormItemLDao.selectApOngoFormItemL */
SELECT

	T.FORM_ID,
	T.FORM_SEQ,
	T.FORM_COMB_SEQ,
	T.ROW_NO,
	T.COL_NO,
	T.CSPN_VA,
	T.ITEM_ID,

FROM AP_ONGO_FORM_ITEM_L T

	AND T.FORM_ID = #{formId, jdbcType=VARCHAR}
	AND T.FORM_SEQ = #{formSeq, jdbcType=INTEGER}
	AND T.FORM_COMB_SEQ = #{formCombSeq, jdbcType=INTEGER}
	AND T.ROW_NO = #{rowNo, jdbcType=INTEGER}
	AND T.COL_NO = #{colNo, jdbcType=INTEGER}
	${whereSqllet}

ORDER BY ${orderBy}
ORDER BY T.FORM_ID, T.FORM_SEQ, T.FORM_COMB_SEQ, T.ROW_NO, T.COL_NO

LIMIT #{pageStrt, jdbcType=NUMERIC}, #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.ap.dao.ApOngoFormItemLDao.countApOngoFormItemL */
SELECT COUNT(*) CNT
FROM AP_ONGO_FORM_ITEM_L T

	AND T.FORM_ID = #{formId, jdbcType=VARCHAR}
	AND T.FORM_SEQ = #{formSeq, jdbcType=INTEGER}
	AND T.FORM_COMB_SEQ = #{formCombSeq, jdbcType=INTEGER}
	AND T.ROW_NO = #{rowNo, jdbcType=INTEGER}
	AND T.COL_NO = #{colNo, jdbcType=INTEGER}
	${whereSqllet}

