mapper.select:
/* com.innobiz.orange.web.ap.dao.ApRescBDao.countApRescB */
SELECT COUNT(*) CNT
FROM AP_RESC_B T

	AND T.RESC_ID = #{rescId, jdbcType=VARCHAR}
	AND T.LANG_TYP_CD = #{langTypCd, jdbcType=VARCHAR}
	${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.ap.dao.ApRescBDao.insertApRescB */ INSERT INTO AP_RESC_B
RESC_ID, LANG_TYP_CD, RESC_VA,
#{rescId, jdbcType=VARCHAR}, #{langTypCd, jdbcType=VARCHAR}, #{rescVa, jdbcType=NVARCHAR},

mapper.update:
/* com.innobiz.orange.web.ap.dao.ApRescBDao.updateApRescB */
UPDATE AP_RESC_B

	RESC_ID = #{rescId, jdbcType=VARCHAR},
	LANG_TYP_CD = #{langTypCd, jdbcType=VARCHAR},
	RESC_VA = #{rescVa, jdbcType=NVARCHAR},


	AND RESC_ID = #{rescId, jdbcType=VARCHAR}
	AND LANG_TYP_CD = #{langTypCd, jdbcType=VARCHAR}
	${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.ap.dao.ApRescBDao.deleteApRescB */
DELETE FROM AP_RESC_B

	AND RESC_ID = #{rescId, jdbcType=VARCHAR}
	AND LANG_TYP_CD = #{langTypCd, jdbcType=VARCHAR}
	${whereSqllet}
