mapper.select:
/* com.innobiz.orange.web.ap.dao.ApStorBDao.countApStorB */
SELECT COUNT(*) CNT
FROM AP_STOR_B T

	AND T.STOR_ID = #{storId, jdbcType=VARCHAR}
	${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.ap.dao.ApStorBDao.insertApStorB */ INSERT INTO AP_STOR_B
STOR_ID, CRT_YN, RESC_ID, ALL_DOC_CNT,
#{storId, jdbcType=VARCHAR}, #{crtYn, jdbcType=VARCHAR}, #{rescId, jdbcType=VARCHAR}, NULL#{allDocCnt, jdbcType=BIGINT},

mapper.update:
/* com.innobiz.orange.web.ap.dao.ApStorBDao.updateApStorB */
UPDATE AP_STOR_B

	STOR_ID = #{storId, jdbcType=VARCHAR},
	CRT_YN = #{crtYn, jdbcType=VARCHAR},
	RESC_ID = #{rescId, jdbcType=VARCHAR},
	ALL_DOC_CNT = NULL#{allDocCnt, jdbcType=BIGINT},


	AND STOR_ID = #{storId, jdbcType=VARCHAR}
	${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.ap.dao.ApStorBDao.deleteApStorB */
DELETE FROM AP_STOR_B

	AND STOR_ID = #{storId, jdbcType=VARCHAR}
	${whereSqllet}
