mapper.select:
/* com.innobiz.orange.web.ap.dao.ApTranFromRDao.selectApTranFromR */
SELECT

	T.TRAN_ID,
	T.FORM_ID,

FROM AP_TRAN_FROM_R T

	AND T.TRAN_ID = #{tranId, jdbcType=VARCHAR}
	AND T.FORM_ID = #{formId, jdbcType=VARCHAR}
	${whereSqllet}

ORDER BY ${orderBy}
ORDER BY T.TRAN_ID, T.FORM_ID

LIMIT #{pageStrt, jdbcType=NUMERIC}, #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.ap.dao.ApTranFromRDao.countApTranFromR */
SELECT COUNT(*) CNT
FROM AP_TRAN_FROM_R T

	AND T.TRAN_ID = #{tranId, jdbcType=VARCHAR}
	AND T.FORM_ID = #{formId, jdbcType=VARCHAR}
	${whereSqllet}

