mapper.select:
/* com.innobiz.orange.web.ap.dao.ApTranTgtTDao.selectApTranTgtT */
SELECT

	T.TRAN_ID,
	T.TRAN_DATA_TYP_CD,
	T.TRAN_SEQ,
	T.APV_NO,

FROM AP_TRAN_TGT_T T

	AND T.TRAN_ID = #{tranId, jdbcType=VARCHAR}
	AND T.TRAN_DATA_TYP_CD = #{tranDataTypCd, jdbcType=VARCHAR}
	AND T.TRAN_SEQ = #{tranSeq, jdbcType=BIGINT}
	${whereSqllet}

ORDER BY ${orderBy}
ORDER BY T.TRAN_ID, T.TRAN_DATA_TYP_CD, T.TRAN_SEQ

LIMIT #{pageStrt, jdbcType=NUMERIC}, #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.ap.dao.ApTranTgtTDao.countApTranTgtT */
SELECT COUNT(*) CNT
FROM AP_TRAN_TGT_T T

	AND T.TRAN_ID = #{tranId, jdbcType=VARCHAR}
	AND T.TRAN_DATA_TYP_CD = #{tranDataTypCd, jdbcType=VARCHAR}
	AND T.TRAN_SEQ = #{tranSeq, jdbcType=BIGINT}
	${whereSqllet}

