TRAN_ID, TRAN_DATA_TYP_CD, TRAN_SEQ, APV_NO,
#{tranId, jdbcType=VARCHAR}, #{tranDataTypCd, jdbcType=VARCHAR}, #{tranSeq, jdbcType=BIGINT}, #{apvNo, jdbcType=BIGINT},

