FROM BA_BULL_FILE_D T
AND T.BB_REF_ID = #{refId, jdbcType=VARCHAR} AND T.FILE_ID = #{fileId, jdbcType=INTEGER} ${whereSqllet}
ORDER BY ${orderBy} ORDER BY T.DISP_ORDR ASC

