mapper.select:
/* com.innobiz.orange.web.bb.dao.BaCatGrpBDao.selectBaCatGrpB */
SELECT

	T.CAT_GRP_ID,
	T.CAT_GRP_NM,
	T.RESC_ID,
	(SELECT RESC_VA FROM BA_RESC_B WHERE RESC_ID = T.RESC_ID AND LANG_TYP_CD = IFNULL(#{queryLang, jdbcType=VARCHAR},'ko')) RESC_NM,
	T.REGR_UID,
	T.REG_DT,
	T.MODR_UID,
	T.MOD_DT,
	T.COMP_ID,

FROM BA_CAT_GRP_B T, BA_RESC_B R

	 AND T.CAT_GRP_ID = #{catGrpId, jdbcType=VARCHAR}
	 AND T.COMP_ID = #{compId, jdbcType=VARCHAR}
	 AND R.LANG_TYP_CD = IFNULL(#{queryLang, jdbcType=VARCHAR},'ko') AND R.RESC_ID = T.RESC_ID AND R.RESC_VA LIKE CONCAT('%', #{schWord, jdbcType=VARCHAR}, '%')
	 ${whereSqllet}

ORDER BY ${orderBy}
ORDER BY T.CAT_GRP_ID DESC

LIMIT #{pageStrt, jdbcType=NUMERIC}, #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.bb.dao.BaCatGrpBDao.countBaCatGrpB */
SELECT COUNT(*) CNT
FROM BA_CAT_GRP_B T, BA_RESC_B R

	 AND T.CAT_GRP_ID = #{catGrpId, jdbcType=VARCHAR}
	 AND T.COMP_ID = #{compId, jdbcType=VARCHAR}
	 AND R.LANG_TYP_CD = IFNULL(#{queryLang, jdbcType=VARCHAR},'ko') AND R.RESC_ID = T.RESC_ID AND R.RESC_VA LIKE CONCAT('%', #{schWord, jdbcType=VARCHAR}, '%')
	 ${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.bb.dao.BaCatGrpBDao.insertBaCatGrpB */ INSERT INTO BA_CAT_GRP_B
CAT_GRP_ID, CAT_GRP_NM, RESC_ID, REGR_UID, REG_DT, MODR_UID, MOD_DT, COMP_ID,
#{catGrpId, jdbcType=VARCHAR}, #{catGrpNm, jdbcType=VARCHAR}, #{rescId, jdbcType=VARCHAR}, #{regrUid, jdbcType=VARCHAR},#{modrUid, jdbcType=VARCHAR}, NOW(),NOW(),NULL, STR_TO_DATE(#{regDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'), #{modrUid, jdbcType=VARCHAR}, NOW(),NULL, STR_TO_DATE(#{modDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'), #{compId, jdbcType=VARCHAR},

mapper.update:
/* com.innobiz.orange.web.pt.dao.BaCatGrpBDao.updateBaCatGrpB */
UPDATE BA_CAT_GRP_B

	 CAT_GRP_NM = #{catGrpNm, jdbcType=VARCHAR},
	 MODR_UID = #{modrUid, jdbcType=VARCHAR},
	
		 MOD_DT = NOW(),
		 MOD_DT = NULL,
		 MOD_DT = STR_TO_DATE(#{modDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'),
	


	 AND CAT_GRP_ID = #{catGrpId, jdbcType=VARCHAR}
	 AND COMP_ID = #{compId, jdbcType=VARCHAR}
	 ${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.bb.dao.BaCatGrpBDao.deleteBaCatGrpB */
DELETE FROM BA_CAT_GRP_B

	AND CAT_GRP_ID = #{catGrpId, jdbcType=VARCHAR}
	 AND COMP_ID = #{compId, jdbcType=VARCHAR}
