mapper.select:
/* com.innobiz.orange.web.bb.dao.BaCdDDao.selectBaCdD */ SELECT
T.CD_ID, T.CD_GRP_ID, T.CD_VA, T.RESC_ID, (SELECT RESC_VA FROM BA_RESC_B WHERE RESC_ID = T.RESC_ID AND LANG_TYP_CD = IFNULL(#{queryLang, jdbcType=VARCHAR},'ko')) RESC_NM, T.SORT_ORDR, T.USE_YN, T.REGR_UID, T.REG_DT, T.MODR_UID, T.MOD_DT,
FROM BA_CD_D T
ORDER BY ${orderBy} ORDER BY T.SORT_ORDR ASC
LIMIT #{pageStrt, jdbcType=NUMERIC}, #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.bb.dao.BaCdDDao.countBaCdD */
SELECT COUNT(*) CNT
FROM BA_CD_D T

mapper.insert:
/* com.innobiz.orange.web.bb.dao.BaCdDDao.insertBaCdD */ INSERT INTO BA_CD_D
CD_ID, CD_GRP_ID, CD_VA, RESC_ID, SORT_ORDR, USE_YN, REGR_UID, REG_DT, MODR_UID, MOD_DT,
#{cdId, jdbcType=VARCHAR}, #{cdGrpId, jdbcType=VARCHAR}, #{cdVa, jdbcType=VARCHAR}, #{rescId, jdbcType=VARCHAR}, #{sortOrdr, jdbcType=INTEGER}, #{useYn, jdbcType=CHAR}, #{regrUid, jdbcType=VARCHAR},#{modrUid, jdbcType=VARCHAR}, NOW(),NOW(),NULL, STR_TO_DATE(#{regDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'), #{modrUid, jdbcType=VARCHAR}, NOW(),NULL, STR_TO_DATE(#{modDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'),

mapper.update:
/* com.innobiz.orange.web.pt.dao.BaCdDDao.updateBaCdD */ UPDATE BA_CD_D
CD_VA = #{cdVa, jdbcType=VARCHAR}, SORT_ORDR = #{sortOrdr, jdbcType=INTEGER}, USE_YN = #{useYn, jdbcType=CHAR}, MODR_UID = #{modrUid, jdbcType=VARCHAR},
MOD_DT = NOW(), MOD_DT = NULL, MOD_DT = STR_TO_DATE(#{modDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'),
AND CD_ID = #{cdId, jdbcType=VARCHAR} ${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.bb.dao.BaCdDDao.deleteBaCdD */
DELETE FROM BA_CD_D

	 AND CD_ID = #{cdId, jdbcType=VARCHAR}
	 AND CD_GRP_ID = #{cdGrpId, jdbcType=VARCHAR}
	 AND 1=0
	 ${whereSqllet}
