mapper.select:
/* com.innobiz.orange.web.bb.dao.BaCdGrpBDao.selectBaCdGrpB */ SELECT
T.CD_GRP_ID, T.COMP_ID, T.CD_GRP_NM, T.RESC_ID, (SELECT RESC_VA FROM BA_RESC_B WHERE RESC_ID = T.RESC_ID AND LANG_TYP_CD = IFNULL(#{queryLang, jdbcType=VARCHAR},'ko')) RESC_NM, T.GRP_USE_YN, T.REGR_UID, T.REG_DT, T.MODR_UID, T.MOD_DT,
FROM BA_CD_GRP_B T, BA_RESC_B R
ORDER BY ${orderBy} ORDER BY T.CD_GRP_ID DESC
LIMIT #{pageStrt, jdbcType=NUMERIC}, #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.bb.dao.BaCdGrpBDao.countBaCdGrpB */
SELECT COUNT(*) CNT
FROM BA_CD_GRP_B T, BA_RESC_B R

mapper.insert:
/* com.innobiz.orange.web.bb.dao.BaCdGrpBDao.insertBaCdGrpB */ INSERT INTO BA_CD_GRP_B
CD_GRP_ID, COMP_ID, CD_GRP_NM, RESC_ID, GRP_USE_YN, REGR_UID, REG_DT, MODR_UID, MOD_DT,
#{cdGrpId, jdbcType=VARCHAR}, #{compId, jdbcType=VARCHAR}, #{cdGrpNm, jdbcType=VARCHAR}, #{rescId, jdbcType=VARCHAR}, #{grpUseYn, jdbcType=CHAR}, #{regrUid, jdbcType=VARCHAR},#{modrUid, jdbcType=VARCHAR}, NOW(),NOW(),NULL, STR_TO_DATE(#{regDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'), #{modrUid, jdbcType=VARCHAR}, NOW(),NULL, STR_TO_DATE(#{modDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'),

mapper.update:
/* com.innobiz.orange.web.pt.dao.BaCdGrpBDao.updateBaCdGrpB */
UPDATE BA_CD_GRP_B

	 CD_GRP_NM = #{cdGrpNm, jdbcType=VARCHAR},
	 GRP_USE_YN = #{grpUseYn, jdbcType=CHAR},
	 MODR_UID = #{modrUid, jdbcType=VARCHAR},
	
		 MOD_DT = NOW(),
		 MOD_DT = NULL,
		 MOD_DT = STR_TO_DATE(#{modDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'),
	


	AND CD_GRP_ID = #{cdGrpId, jdbcType=VARCHAR}
	 AND COMP_ID = #{compId, jdbcType=VARCHAR}
	 ${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.bb.dao.BaCdGrpBDao.deleteBaCdGrpB */
DELETE FROM BA_CD_GRP_B

	AND CD_GRP_ID = #{cdGrpId, jdbcType=VARCHAR}
	 AND COMP_ID = #{compId, jdbcType=VARCHAR}
