mapper.select:
/* com.innobiz.orange.web.bb.dao.BaMyBullMDao.selectBaMyBullM */
SELECT

	T.USER_UID,
	T.BRD_ID,
	T.RESC_ID,
	(SELECT RESC_VA FROM BA_RESC_B WHERE RESC_ID = T.RESC_ID AND LANG_TYP_CD = IFNULL(#{queryLang, jdbcType=VARCHAR},'ko')) RESC_NM,

FROM BA_MY_BULL_M T

	AND USER_UID = #{userUid, jdbcType=VARCHAR}
	 ${whereSqllet}

ORDER BY ${orderBy}
ORDER BY T.BRD_ID ASC

/* com.innobiz.orange.web.bb.dao.BaMyBullMDao.countBaMyBullM */
SELECT COUNT(*) CNT
FROM BA_MY_BULL_M

	AND USER_UID = #{userUid, jdbcType=VARCHAR}
	 ${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.bb.dao.BaMyBullMDao.insertBaMyBullM */ INSERT INTO BA_MY_BULL_M
USER_UID, BRD_ID, RESC_ID,
#{userUid, jdbcType=VARCHAR}, #{brdId, jdbcType=VARCHAR}, #{rescId, jdbcType=VARCHAR},

mapper.delete:
/* com.innobiz.orange.web.bb.dao.BaMyBullMDao.deleteBaMyBullM */
DELETE FROM BA_MY_BULL_M

	 AND USER_UID = #{userUid, jdbcType=VARCHAR}
	 AND BRD_ID = #{brdId, jdbcType=VARCHAR}
	 AND 1=0
	 ${whereSqllet}
