mapper.select:
/* com.innobiz.orange.web.bb.dao.BaRecmdHstLDao.selectBaRecmdHstL */
SELECT

	T.BULL_ID,
	T.USER_UID,
	(SELECT RESC_VA FROM OR_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM OR_USER_B WHERE USER_UID = T.USER_UID) AND LANG_TYP_CD = IFNULL(#{queryLang, jdbcType=VARCHAR}, 'ko')) USER_NM,
	T.REG_DT,

FROM BA_RECMD_HST_L T

	 AND T.BULL_ID = #{bullId, jdbcType=INTEGER}
	 AND T.USER_UID = #{userUid, jdbcType=VARCHAR}
	 ${whereSqllet}

ORDER BY ${orderBy}
ORDER BY T.REG_DT DESC

LIMIT #{pageStrt, jdbcType=NUMERIC}, #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.bb.dao.BaRecmdHstLDao.countBaRecmdHstL */
SELECT COUNT(*) CNT
FROM BA_RECMD_HST_L T

	 AND T.BULL_ID = #{bullId, jdbcType=INTEGER}
	 AND T.USER_UID = #{userUid, jdbcType=VARCHAR}
	 ${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.bb.dao.BaRecmdHstLDao.insertBaRecmdHstL */ INSERT INTO BA_RECMD_HST_L
BULL_ID, USER_UID, REG_DT,
#{bullId, jdbcType=VARCHAR}, #{userUid, jdbcType=VARCHAR}, NOW(),

mapper.update:
/* com.innobiz.orange.web.bb.dao.BaRecmdHstLDao.updateBaRecmdHstL */
UPDATE BA_RECMD_HST_L

	REG_DT = NOW(),


	 AND BULL_ID = #{bullId, jdbcType=INTEGER}
	 AND USER_UID = #{userUid, jdbcType=VARCHAR}
	 AND 1=0
	 ${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.bb.dao.BaRecmdHstLDao.deleteBaRecmdHstL */
DELETE FROM BA_RECMD_HST_L

	 AND BULL_ID = #{bullId, jdbcType=INTEGER}
	 AND USER_UID = #{userUid, jdbcType=VARCHAR}
	 AND 1=0
	 ${whereSqllet}
