mapper.select:
/* com.innobiz.orange.web.bb.dao.BaTblBDao.selectBaTblB */ SELECT
T.TBL_ID, T.TBL_NM, T.TBL_DISP_NM, T.RESC_ID, (SELECT RESC_VA FROM BA_RESC_B WHERE RESC_ID = T.RESC_ID AND LANG_TYP_CD = IFNULL(#{queryLang, jdbcType=VARCHAR},'ko')) RESC_NM, T.EX_YN, T.REGR_UID, T.REG_DT, T.MODR_UID, T.MOD_DT, T.COMP_ID,
FROM BA_TBL_B T, BA_RESC_B R
AND T.TBL_ID = #{tblId, jdbcType=VARCHAR} AND T.COMP_ID = #{compId, jdbcType=VARCHAR} AND R.LANG_TYP_CD = IFNULL(#{queryLang, jdbcType=VARCHAR},'ko') AND R.RESC_ID = T.RESC_ID AND R.RESC_VA LIKE CONCAT('%', #{schWord, jdbcType=VARCHAR}, '%') AND CONCAT('BB_', T.TBL_NM, '_L') LIKE CONCAT('%', #{schWord, jdbcType=VARCHAR}, '%') ${whereSqllet}
ORDER BY ${orderBy} ORDER BY T.TBL_ID DESC
LIMIT #{pageStrt, jdbcType=NUMERIC}, #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.bb.dao.BaTblBDao.countBaTblB */
SELECT COUNT(*) CNT
FROM BA_TBL_B T, BA_RESC_B R

	 AND T.TBL_ID = #{tblId, jdbcType=VARCHAR}
	 AND T.COMP_ID = #{compId, jdbcType=VARCHAR}
	 AND R.LANG_TYP_CD = IFNULL(#{queryLang, jdbcType=VARCHAR},'ko') AND R.RESC_ID = T.RESC_ID AND R.RESC_VA LIKE CONCAT('%', #{schWord, jdbcType=VARCHAR}, '%')
	 AND CONCAT('BB_', T.TBL_NM, '_L') LIKE CONCAT('%', #{schWord, jdbcType=VARCHAR}, '%')
	 ${whereSqllet}

