mapper.update:
/* com.innobiz.orange.web.pt.dao.BaTblBDao.updateBaTblB */
UPDATE BA_TBL_B

	 TBL_DISP_NM = #{tblDispNm, jdbcType=VARCHAR},
	 EX_YN = #{exYn, jdbcType=VARCHAR},
	 MODR_UID = #{modrUid, jdbcType=VARCHAR},
	
		 MOD_DT = NOW(),
		 MOD_DT = NULL,
		 MOD_DT = STR_TO_DATE(#{modDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'),
	


	 AND TBL_ID = #{tblId, jdbcType=VARCHAR}
	 AND COMP_ID = #{compId, jdbcType=VARCHAR}
	 ${whereSqllet}

