mapper.select:
/* com.innobiz.orange.web.ct.dao.CtFileDDao.selectCtFileD */
SELECT
CFD_T.FILE_ID, CFD_T.REF_ID, CFD_T.DISP_NM, CFD_T.DISP_ORDR, CFD_T.FILE_EXT, CFD_T.FILE_SIZE, CFD_T.SAVE_PATH, CFD_T.USE_YN, CFD_T.REGR_UID, CFD_T.REG_DT, CFD_T.CT_ID, CFD_T.CT_FNC_UID,
FROM CT_FILE_D CFD_T
ORDER BY ${orderBy} ORDER BY CFD_T.FILE_ID
LIMIT #{pageStrt, jdbcType=NUMERIC}, #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.ct.dao.CtFileDDao.countCtFileD */
SELECT COUNT(*) CNT
FROM CT_FILE_D CFD_T

/* com.innobiz.orange.web.ct.dao.CtFileDDao.countCtFileDSum */
SELECT SUM(FILE_SIZE) FILE_SUM
FROM CT_FILE_D CFD_T

mapper.insert:
/* com.innobiz.orange.web.ct.dao.CtFileDDao.insertCtFileD */ INSERT INTO CT_FILE_D
FILE_ID, REF_ID, DISP_NM, DISP_ORDR, FILE_EXT, FILE_SIZE, SAVE_PATH, USE_YN, REGR_UID, REG_DT, CT_ID, CT_FNC_UID,
#{fileId, jdbcType=INTEGER}, #{refId, jdbcType=VARCHAR}, #{dispNm, jdbcType=NVARCHAR}, #{dispOrdr, jdbcType=INTEGER}, #{fileExt, jdbcType=VARCHAR}, #{fileSize, jdbcType=INTEGER}, #{savePath, jdbcType=VARCHAR}, #{useYn, jdbcType=VARCHAR}, #{regrUid, jdbcType=VARCHAR}, NOW(),STR_TO_DATE(#{regDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'), #{ctId, jdbcType=VARCHAR}, #{ctFncUid, jdbcType=VARCHAR},

mapper.update:
/* com.innobiz.orange.web.ct.dao.CtFileDDao.updateCtFileD */
UPDATE CT_FILE_D

	 REF_ID = #{newRefId, jdbcType=VARCHAR},
	 DISP_ORDR = #{dispOrdr, jdbcType=INTEGER},
	 USE_YN = #{useYn, jdbcType=VARCHAR},
	 CT_ID = #{ctId, jdbcType=VARCHAR},
	 CT_FNC_UID = #{ctFncUid, jdbcType=VARCHAR},		


	 AND FILE_ID = #{fileId, jdbcType=NUMERIC}
	 AND REF_ID = #{refId, jdbcType=VARCHAR}
	 AND 1=0
	 ${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.ct.dao.CtFileDDao.deleteCtFileD */
DELETE FROM CT_FILE_D

	 AND FILE_ID = #{fileId, jdbcType=NUMERIC}
	 AND REF_ID = #{refId, jdbcType=VARCHAR}
	 AND 1=0
	 ${whereSqllet}
