mapper.select:
/* com.innobiz.orange.web.ct.dao.CtQuesExamDDao.selectCtQuesExamD */
SELECT
CQED_T.QUES_ID, CQED_T.COMP_ID, CQED_T.SURV_ID, CQED_T.EXAM_NO, CQED_T.EXAM_ORDR, CQED_T.EXAM_DISP_NM, CQED_T.INPUT_YN, CQED_T.EXAM_IMG_USE_YN, CQED_T.IMG_SURV_FILE_ID, (SELECT SAVE_PATH FROM CT_FILE_D WHERE FILE_ID=CQED_T.IMG_SURV_FILE_ID LIMIT 0, 1 ) IMG_SAVE_PATH
FROM CT_QUES_EXAM_D CQED_T
ORDER BY ${orderBy} ORDER BY CQED_T.EXAM_NO
LIMIT #{pageStrt, jdbcType=NUMERIC}, #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.ct.dao.CtQuesExamDDao.countCtQuesExamD */
SELECT COUNT(*) CNT
FROM CT_QUES_EXAM_D CQED_T

mapper.insert:
/* com.innobiz.orange.web.ct.dao.CtQuesExamDDao.insertCtQuesExamD */ INSERT INTO CT_QUES_EXAM_D
QUES_ID, COMP_ID, SURV_ID, EXAM_NO, EXAM_ORDR, EXAM_DISP_NM, INPUT_YN, EXAM_IMG_USE_YN, IMG_SURV_FILE_ID,
#{quesId, jdbcType=VARCHAR},#{compId, jdbcType=CHAR},#{survId, jdbcType=VARCHAR}, #{examNo, jdbcType=NUMERIC},#{examOrdr, jdbcType=NUMERIC},#{examDispNm, jdbcType=NVARCHAR}, #{inputYn, jdbcType=CHAR},#{examImgUseYn, jdbcType=CHAR},#{imgSurvFileId, jdbcType=VARCHAR},

mapper.update:
		/* com.innobiz.orange.web.ct.dao.CtQuesExamDDao.updateCtQuesExamD */
		UPDATE CT_QUES_EXAM_D
		
			 EXAM_ORDR = #{examOrdr, jdbcType=NUMERIC},
			 EXAM_DISP_NM = #{examDispNm, jdbcType=VARCHAR},
			 INPUT_YN = #{inputYn, jdbcType=VARCHAR},
			 EXAM_IMG_USE_YN = #{examImgUseYn, jdbcType=VARCHAR},
			 IMG_SURV_FILE_ID = #{imgSurvFileId, jdbcType=VARCHAR},
		
		
		     AND QUES_ID = #{quesId, jdbcType=VARCHAR}
		     AND COMP_ID = #{compId, jdbcType=CHAR}
   		     AND SURV_ID = #{survId, jdbcType=VARCHAR}
   		     AND EXAM_NO = #{examNo, jdbcType=NUMERIC}

mapper.delete:
		/* com.innobiz.orange.web.ct.dao.CtQuesExamDDao.deleteCtQuesExamD */
		DELETE FROM CT_QUES_EXAM_D
		
		     AND QUES_ID = #{quesId, jdbcType=VARCHAR}
		     AND COMP_ID = #{compId, jdbcType=CHAR}
   		     AND SURV_ID = #{survId, jdbcType=VARCHAR}
   		     AND EXAM_NO = #{examNo, jdbcType=NUMERIC}
