mapper.select:
/* com.innobiz.orange.web.dm.dao.DmBumkBDao.selectDmBumkB */ SELECT
T.BUMK_ID, (SELECT RESC_VA FROM DM_COMM_RESC_B WHERE RESC_ID = T.RESC_ID AND LANG_TYP_CD = IFNULL(#{queryLang, jdbcType=VARCHAR},'ko')) AS BUMK_NM, T.RESC_ID, T.BUMK_CAT, T.SORT_ORDR, T.REGR_UID, T.REG_DT, T.MODR_UID, T.MOD_DT,
FROM DM_BUMK_B T
ORDER BY ${orderBy} ORDER BY T.REG_DT
LIMIT #{pageStrt, jdbcType=NUMERIC}, #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.dm.dao.DmBumkBDao.countDmBumkB */
SELECT COUNT(*) CNT
FROM DM_BUMK_B T

mapper.insert:
/* com.innobiz.orange.web.dm.dao.DmBumkBDao.insertDmBumkB */ INSERT INTO DM_BUMK_B
BUMK_ID, BUMK_NM, RESC_ID, BUMK_CAT, SORT_ORDR, REGR_UID, REG_DT, MODR_UID, MOD_DT,
#{bumkId, jdbcType=VARCHAR}, #{bumkNm, jdbcType=NVARCHAR}, #{rescId, jdbcType=VARCHAR}, #{bumkCat, jdbcType=VARCHAR}, #{sortOrdr, jdbcType=INTEGER}, #{regrUid, jdbcType=VARCHAR},NULL, NOW(),NULL, STR_TO_DATE(#{regDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'), #{modrUid, jdbcType=VARCHAR},NULL, NOW(),NULL, STR_TO_DATE(#{modDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'),

mapper.update:
/* com.innobiz.orange.web.dm.dao.DmBumkBDao.updateDmBumkB */ UPDATE DM_BUMK_B
BUMK_NM = #{bumkNm, jdbcType=VARCHAR}, BUMK_CAT = #{bumkCat, jdbcType=VARCHAR}, SORT_ORDR = #{sortOrdr, jdbcType=INTEGER}, MODR_UID = #{modrUid, jdbcType=VARCHAR},
MOD_DT = NOW(), MOD_DT = NULL, MOD_DT = STR_TO_DATE(#{modDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'),
AND BUMK_ID = #{bumkId, jdbcType=VARCHAR} AND REGR_UID = #{regrUid, jdbcType=VARCHAR}

mapper.delete:
/* com.innobiz.orange.web.dm.dao.DmBumkBDao.deleteDmBumkB */
DELETE FROM DM_BUMK_B

	 AND BUMK_ID = #{bumkId, jdbcType=VARCHAR}
	 AND REGR_UID = #{regrUid, jdbcType=VARCHAR}
