mapper.select:
/* com.innobiz.orange.web.dm.dao.DmBumkLDao.selectDmBumkL */ SELECT
T.STOR_ID, T.BUMK_ID, T.REG_CAT, T.CAT_VA, T.SORT_ORDR,
FROM DM_BUMK_L T
ORDER BY ${orderBy} ORDER BY T.BUMK_ID
LIMIT #{pageStrt, jdbcType=NUMERIC}, #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.dm.dao.DmBumkLDao.countDmBumkL */
SELECT COUNT(*) CNT
FROM DM_BUMK_L T

mapper.insert:
/* com.innobiz.orange.web.dm.dao.DmBumkLDao.insertDmBumkL */ INSERT INTO DM_BUMK_L
STOR_ID, BUMK_ID, REG_CAT, CAT_VA, SORT_ORDR, REGR_UID, REG_DT, MODR_UID, MOD_DT,
#{storId, jdbcType=VARCHAR}, #{bumkId, jdbcType=VARCHAR}, #{regCat, jdbcType=VARCHAR}, #{catVa, jdbcType=VARCHAR}, #{sortOrdr, jdbcType=INTEGER}, #{regrUid, jdbcType=VARCHAR},NULL, NOW(),NULL, STR_TO_DATE(#{regDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'), #{modrUid, jdbcType=VARCHAR},NULL, NOW(),NULL, STR_TO_DATE(#{modDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'),

mapper.update:
/* com.innobiz.orange.web.dm.dao.DmBumkLDao.updateDmBumkL */
UPDATE DM_BUMK_L

	 SORT_ORDR = #{sortOrdr, jdbcType=INTEGER},
	 MODR_UID = #{modrUid, jdbcType=VARCHAR},
	 
		 MOD_DT = NOW(),
		 MOD_DT = NULL,
		 MOD_DT = STR_TO_DATE(#{modDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'),
	


	 AND STOR_ID = #{storId, jdbcType=VARCHAR}
	 AND BUMK_ID = #{bumkId, jdbcType=VARCHAR}
	 AND REG_CAT = #{regCat, jdbcType=VARCHAR}
	 AND CAT_VA = #{catVa, jdbcType=VARCHAR}

mapper.delete:
/* com.innobiz.orange.web.dm.dao.DmBumkLDao.deleteDmBumkL */
DELETE FROM DM_BUMK_L

	 AND STOR_ID = #{storId, jdbcType=VARCHAR}
	 AND BUMK_ID = #{bumkId, jdbcType=VARCHAR}
	 AND REG_CAT = #{regCat, jdbcType=VARCHAR}
	 AND CAT_VA = #{catVa, jdbcType=VARCHAR}
