mapper.select:
/* com.innobiz.orange.web.dm.dao.DmRescBDao.selectDmRescB */
SELECT

	T.STOR_ID,
	T.RESC_ID,
	T.LANG_TYP_CD,
	T.RESC_VA ,

FROM DM_${prefix}RESC_B T

ORDER BY ${orderBy}
ORDER BY T.RESC_ID

LIMIT #{pageStrt, jdbcType=NUMERIC}, #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.dm.dao.DmRescBDao.countDmRescB */
SELECT COUNT(*) CNT
FROM DM_${prefix}RESC_B T

mapper.insert:
/* com.innobiz.orange.web.dm.dao.DmRescBDao.insertDmRescB */ INSERT INTO DM_${prefix}RESC_B
STOR_ID, RESC_ID, LANG_TYP_CD, RESC_VA ,
#{storId, jdbcType=VARCHAR}, #{rescId, jdbcType=VARCHAR}, #{langTypCd, jdbcType=VARCHAR}, #{rescVa, jdbcType=VARCHAR},

mapper.update:
/* com.innobiz.orange.web.dm.dao.DmRescBDao.updateDmRescB */
UPDATE DM_${prefix}RESC_B

	 RESC_VA = #{rescVa, jdbcType=VARCHAR},


	 AND STOR_ID = #{storId, jdbcType=VARCHAR}
	 AND RESC_ID = #{rescId, jdbcType=VARCHAR}
	 AND LANG_TYP_CD = #{langTypCd, jdbcType=VARCHAR}

mapper.delete:
/* com.innobiz.orange.web.dm.dao.DmRescBDao.deleteDmRescB */
DELETE FROM DM_${prefix}RESC_B

	 AND STOR_ID = #{storId, jdbcType=VARCHAR}
	 AND RESC_ID = #{rescId, jdbcType=VARCHAR}
	 AND LANG_TYP_CD = #{langTypCd, jdbcType=VARCHAR}
