mapper.select:
/* com.innobiz.orange.web.dm.dao.DmTranTgtTDao.selectDmTranTgtT */
SELECT

	T.TRAN_ID,
	T.TRAN_SEQ,
	T.DOC_GRP_ID,

FROM DM_TRAN_TGT_T T

	AND T.TRAN_ID = #{tranId, jdbcType=VARCHAR}
	AND T.TRAN_SEQ = #{tranSeq, jdbcType=BIGINT}
	${whereSqllet}

ORDER BY ${orderBy}
ORDER BY T.TRAN_ID, T.TRAN_DATA_TYP_CD, T.TRAN_SEQ

LIMIT #{pageStrt, jdbcType=NUMERIC}, #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.dm.dao.DmTranTgtTDao.countDmTranTgtT */
SELECT COUNT(*) CNT
FROM DM_TRAN_TGT_T T

	AND T.TRAN_ID = #{tranId, jdbcType=VARCHAR}
	AND T.TRAN_SEQ = #{tranSeq, jdbcType=BIGINT}
	${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.dm.dao.DmTranTgtTDao.insertDmTranTgtT */ INSERT INTO DM_TRAN_TGT_T
TRAN_ID, TRAN_SEQ, DOC_GRP_ID,
#{tranId, jdbcType=VARCHAR}, #{tranSeq, jdbcType=BIGINT}, #{docGrpId, jdbcType=BIGINT},

mapper.update:
/* com.innobiz.orange.web.dm.dao.DmTranTgtTDao.updateDmTranTgtT */
UPDATE DM_TRAN_TGT_T

	TRAN_ID = #{tranId, jdbcType=VARCHAR},
	TRAN_SEQ = #{tranSeq, jdbcType=BIGINT},
	DOC_GRP_ID = #{docGrpId, jdbcType=BIGINT},


	AND TRAN_ID = #{tranId, jdbcType=VARCHAR}
	AND TRAN_SEQ = #{tranSeq, jdbcType=BIGINT}
	${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.dm.dao.DmTranTgtTDao.deleteDmTranTgtT */
DELETE FROM DM_TRAN_TGT_T

	AND TRAN_ID = #{tranId, jdbcType=VARCHAR}
	AND TRAN_SEQ = #{tranSeq, jdbcType=BIGINT}
	${whereSqllet}
