mapper.select:
/* com.innobiz.orange.web.or.dao.OrOdurSecuDDao.selectOrOdurSecuD */
SELECT

	T.ODUR_UID,
	T.SECU_ID,
	T.SECU_VA,

FROM OR_ODUR_SECU_D T

	AND T.ODUR_UID = #{odurUid, jdbcType=VARCHAR}
	AND T.SECU_ID = #{secuId, jdbcType=VARCHAR}
	${whereSqllet}

ORDER BY ${orderBy}
ORDER BY T.ODUR_UID, T.SECU_ID

LIMIT #{pageStrt, jdbcType=NUMERIC}, #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.or.dao.OrOdurSecuDDao.countOrOdurSecuD */
SELECT COUNT(*) CNT
FROM OR_ODUR_SECU_D T

	AND T.ODUR_UID = #{odurUid, jdbcType=VARCHAR}
	AND T.SECU_ID = #{secuId, jdbcType=VARCHAR}
	${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.or.dao.OrOdurSecuDDao.insertOrOdurSecuD */ INSERT INTO OR_ODUR_SECU_D
ODUR_UID, SECU_ID, SECU_VA,
#{odurUid, jdbcType=VARCHAR}, #{secuId, jdbcType=VARCHAR}, #{secuVa, jdbcType=NVARCHAR},

mapper.update:
/* com.innobiz.orange.web.or.dao.OrOdurSecuDDao.updateOrOdurSecuD */
UPDATE OR_ODUR_SECU_D

	ODUR_UID = #{odurUid, jdbcType=VARCHAR},
	SECU_ID = #{secuId, jdbcType=VARCHAR},
	SECU_VA = #{secuVa, jdbcType=NVARCHAR},


	AND ODUR_UID = #{odurUid, jdbcType=VARCHAR}
	AND SECU_ID = #{secuId, jdbcType=VARCHAR}
	${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.or.dao.OrOdurSecuDDao.deleteOrOdurSecuD */
DELETE FROM OR_ODUR_SECU_D

	AND ODUR_UID = #{odurUid, jdbcType=VARCHAR}
	AND SECU_ID = #{secuId, jdbcType=VARCHAR}
	${whereSqllet}
