mapper.select:
/* com.innobiz.orange.web.or.dao.OrOrgBDao.countOrOrgB */
SELECT COUNT(*) CNT
FROM OR_ORG_B T

	AND T.ORG_ID = #{orgId, jdbcType=VARCHAR}
	AND T.COMP_ID = #{compId, jdbcType=VARCHAR}
	AND T.ORG_PID = #{orgPid, jdbcType=VARCHAR}
	AND T.RID = #{rid, jdbcType=VARCHAR}
	AND T.USE_YN = #{useYn, jdbcType=VARCHAR}
	AND T.SYS_ORG_YN = #{sysOrgYn, jdbcType=VARCHAR}
	AND T.COMP_ID IN ( #{item, jdbcType=VARCHAR} )
	AND T.COMP_ID != #{foreignCompId, jdbcType=VARCHAR}
	${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.or.dao.OrOrgBDao.insertOrOrgB */ INSERT INTO OR_ORG_B
ORG_ID, ORG_NM, RESC_ID, COMP_ID, ORG_PID, DEPT_ID, DEPT_RESC_ID, RID, SORT_ORDR, ORG_TYP_CD, SYNC_STEP_CD, USE_YN, SYS_ORG_YN, ORG_ABBR_RESC_ID, REF_VA1, REF_VA2, REF_VA3, REGR_UID, REG_DT, MODR_UID, MOD_DT,
#{orgId, jdbcType=VARCHAR}, #{orgNm, jdbcType=NVARCHAR}, #{rescId, jdbcType=VARCHAR}, #{compId, jdbcType=VARCHAR}, #{orgPid, jdbcType=VARCHAR}, #{deptId, jdbcType=VARCHAR}, #{deptRescId, jdbcType=VARCHAR}, #{rid, jdbcType=VARCHAR}, #{sortOrdr, jdbcType=INTEGER}, (SELECT IFNULL(MAX(P.SORT_ORDR),0)+1 FROM OR_ORG_B P WHERE P.COMP_ID = #{compId, jdbcType=VARCHAR} AND P.ORG_PID = #{orgPid, jdbcType=VARCHAR}), #{orgTypCd, jdbcType=VARCHAR}, #{syncStepCd, jdbcType=VARCHAR}, #{useYn, jdbcType=VARCHAR}, #{sysOrgYn, jdbcType=VARCHAR}, #{orgAbbrRescId, jdbcType=VARCHAR}, #{refVa1, jdbcType=NVARCHAR}, #{refVa2, jdbcType=NVARCHAR}, #{refVa3, jdbcType=NVARCHAR}, #{regrUid, jdbcType=VARCHAR},#{modrUid, jdbcType=VARCHAR}, NOW(),NOW(),NULL, STR_TO_DATE(#{regDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'), #{modrUid, jdbcType=VARCHAR}, NOW(),NULL, STR_TO_DATE(#{modDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'),

mapper.update:
/* com.innobiz.orange.web.or.dao.OrOrgBDao.updateOrOrgB */ UPDATE OR_ORG_B
ORG_ID = #{orgId, jdbcType=VARCHAR}, ORG_NM = #{orgNm, jdbcType=NVARCHAR}, RESC_ID = #{rescId, jdbcType=VARCHAR}, COMP_ID = #{compId, jdbcType=VARCHAR}, ORG_PID = #{orgPid, jdbcType=VARCHAR}, DEPT_ID = #{deptId, jdbcType=VARCHAR}, DEPT_RESC_ID = #{deptRescId, jdbcType=VARCHAR}, RID = #{rid, jdbcType=VARCHAR}, SORT_ORDR = #{sortOrdr, jdbcType=INTEGER}, ORG_TYP_CD = #{orgTypCd, jdbcType=VARCHAR}, SYNC_STEP_CD = #{syncStepCd, jdbcType=VARCHAR}, USE_YN = #{useYn, jdbcType=VARCHAR}, SYS_ORG_YN = #{sysOrgYn, jdbcType=VARCHAR}, ORG_ABBR_RESC_ID = #{orgAbbrRescId, jdbcType=VARCHAR}, REF_VA1 = #{refVa1, jdbcType=NVARCHAR}, REF_VA2 = #{refVa2, jdbcType=NVARCHAR}, REF_VA3 = #{refVa3, jdbcType=NVARCHAR}, REGR_UID = #{regrUid, jdbcType=VARCHAR},
REG_DT = NOW(), REG_DT = NULL, REG_DT = STR_TO_DATE(#{regDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'),
MODR_UID = #{modrUid, jdbcType=VARCHAR},
MOD_DT = NOW(), MOD_DT = NULL, MOD_DT = STR_TO_DATE(#{modDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'),
AND ORG_ID = #{orgId, jdbcType=VARCHAR} AND ORG_ID IN ( #{item, jdbcType=VARCHAR} ) ${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.or.dao.OrOrgBDao.deleteOrOrgB */
DELETE FROM OR_ORG_B

	AND ORG_ID = #{orgId, jdbcType=VARCHAR}
	AND COMP_ID = #{compId, jdbcType=VARCHAR}
	${whereSqllet}
