mapper.select:
/* com.innobiz.orange.web.pt.dao.PtAuthGrpBDao.countPtAuthGrpB */
SELECT COUNT(*) CNT
FROM PT_AUTH_GRP_B T

	AND T.COMP_ID = #{compId, jdbcType=VARCHAR}
	AND T.AUTH_GRP_ID = #{authGrpId, jdbcType=VARCHAR}
	AND T.AUTH_GRP_TYP_CD = #{authGrpTypCd, jdbcType=VARCHAR}
	AND T.AUTH_GRP_CAT_CD = #{authGrpCatCd, jdbcType=VARCHAR}
	AND T.USE_YN = #{useYn, jdbcType=VARCHAR}
	${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.pt.dao.PtAuthGrpBDao.insertPtAuthGrpB */ INSERT INTO PT_AUTH_GRP_B
COMP_ID, AUTH_GRP_ID, AUTH_GRP_TYP_CD, AUTH_GRP_CAT_CD, AUTH_GRP_NM, RESC_ID, AUTH_GRP_DESC, SYS_AUTH_GRP_YN, USE_YN, SORT_ORDR, REGR_UID, REG_DT, MODR_UID, MOD_DT,
#{compId, jdbcType=VARCHAR}, #{authGrpId, jdbcType=VARCHAR}, #{authGrpTypCd, jdbcType=VARCHAR}, #{authGrpCatCd, jdbcType=VARCHAR}, #{authGrpNm, jdbcType=NVARCHAR}, #{rescId, jdbcType=VARCHAR}, #{authGrpDesc, jdbcType=NVARCHAR}, #{sysAuthGrpYn, jdbcType=VARCHAR}, #{useYn, jdbcType=VARCHAR}, #{sortOrdr, jdbcType=INTEGER}, (SELECT IFNULL(MAX(P.SORT_ORDR),0)+1 FROM PT_AUTH_GRP_B P WHERE P.COMP_ID = #{compId, jdbcType=VARCHAR} AND P.AUTH_GRP_CAT_CD = #{authGrpCatCd, jdbcType=VARCHAR}), #{regrUid, jdbcType=VARCHAR},#{modrUid, jdbcType=VARCHAR}, NOW(),NOW(),NULL, STR_TO_DATE(#{regDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'), #{modrUid, jdbcType=VARCHAR}, NOW(),NULL, STR_TO_DATE(#{modDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'),

mapper.update:
/* com.innobiz.orange.web.pt.dao.PtAuthGrpBDao.updatePtAuthGrpB */ UPDATE PT_AUTH_GRP_B
COMP_ID = #{compId, jdbcType=VARCHAR}, AUTH_GRP_ID = #{authGrpId, jdbcType=VARCHAR}, AUTH_GRP_TYP_CD = #{authGrpTypCd, jdbcType=VARCHAR}, AUTH_GRP_CAT_CD = #{authGrpCatCd, jdbcType=VARCHAR}, AUTH_GRP_NM = #{authGrpNm, jdbcType=NVARCHAR}, RESC_ID = #{rescId, jdbcType=VARCHAR}, AUTH_GRP_DESC = #{authGrpDesc, jdbcType=NVARCHAR}, SYS_AUTH_GRP_YN = #{sysAuthGrpYn, jdbcType=VARCHAR}, USE_YN = #{useYn, jdbcType=VARCHAR}, SORT_ORDR = #{sortOrdr, jdbcType=INTEGER}, REGR_UID = #{regrUid, jdbcType=VARCHAR},
REG_DT = NOW(), REG_DT = NULL, REG_DT = STR_TO_DATE(#{regDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'),
MODR_UID = #{modrUid, jdbcType=VARCHAR},
MOD_DT = NOW(), MOD_DT = NULL, MOD_DT = STR_TO_DATE(#{modDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'),
AND COMP_ID = #{compId, jdbcType=VARCHAR} AND AUTH_GRP_ID = #{authGrpId, jdbcType=VARCHAR} ${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.pt.dao.PtAuthGrpBDao.deletePtAuthGrpB */
