mapper.select:
/* com.innobiz.orange.web.pt.dao.PtCacheTDao.selectPtCacheT */
SELECT

	T.CACHE_CAT_ID,
	T.LANG_TYP_CD,
	T.CACHE_ID,
	DATE_FORMAT(T.MOD_DT, '%Y-%m-%d %H:%i:%s') MOD_DT,

FROM PT_CACHE_T T

	AND T.CACHE_CAT_ID = #{cacheCatId, jdbcType=VARCHAR}
	AND T.LANG_TYP_CD = #{langTypCd, jdbcType=VARCHAR}
	AND T.CACHE_ID = #{cacheId, jdbcType=VARCHAR}
	AND MOD_DT > STR_TO_DATE(#{modDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s')
	${whereSqllet}

ORDER BY ${orderBy}
ORDER BY T.CACHE_CAT_ID, T.LANG_TYP_CD, T.CACHE_ID

LIMIT #{pageStrt, jdbcType=NUMERIC}, #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.pt.dao.PtCacheTDao.countPtCacheT */
SELECT COUNT(*) CNT
FROM PT_CACHE_T T

	AND T.CACHE_CAT_ID = #{cacheCatId, jdbcType=VARCHAR}
	AND T.LANG_TYP_CD = #{langTypCd, jdbcType=VARCHAR}
	AND T.CACHE_ID = #{cacheId, jdbcType=VARCHAR}
	AND MOD_DT > STR_TO_DATE(#{modDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s')
	${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.pt.dao.PtCacheTDao.insertPtCacheT */ INSERT INTO PT_CACHE_T
CACHE_CAT_ID, LANG_TYP_CD, CACHE_ID, MOD_DT,
#{cacheCatId, jdbcType=VARCHAR}, #{langTypCd, jdbcType=VARCHAR}, #{cacheId, jdbcType=VARCHAR}, NOW(),NULL, STR_TO_DATE(#{modDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'),

mapper.update:
/* com.innobiz.orange.web.pt.dao.PtCacheTDao.updatePtCacheT */
UPDATE PT_CACHE_T

	CACHE_CAT_ID = #{cacheCatId, jdbcType=VARCHAR},
	LANG_TYP_CD = #{langTypCd, jdbcType=VARCHAR},
	CACHE_ID = #{cacheId, jdbcType=VARCHAR},
	
		MOD_DT = NOW(),
		MOD_DT = NULL,
		MOD_DT = STR_TO_DATE(#{modDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'),
	


	AND CACHE_CAT_ID = #{cacheCatId, jdbcType=VARCHAR}
	AND LANG_TYP_CD = #{langTypCd, jdbcType=VARCHAR}
	AND CACHE_ID = #{cacheId, jdbcType=VARCHAR}
	${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.pt.dao.PtCacheTDao.deletePtCacheT */
DELETE FROM PT_CACHE_T

	AND CACHE_CAT_ID = #{cacheCatId, jdbcType=VARCHAR}
	AND LANG_TYP_CD = #{langTypCd, jdbcType=VARCHAR}
	AND CACHE_ID = #{cacheId, jdbcType=VARCHAR}
	${whereSqllet}
