mapper.select:
/* com.innobiz.orange.web.pt.dao.PtCdBDao.countPtCdB */
SELECT COUNT(*) CNT
FROM PT_CD_B T

	AND T.CLS_CD = #{clsCd, jdbcType=VARCHAR}
	AND T.CD = #{cd, jdbcType=VARCHAR}
	AND T.REF_VA1 = #{refVa1, jdbcType=NVARCHAR}
	AND T.REF_VA2 = #{refVa2, jdbcType=NVARCHAR}
	AND T.USE_YN = #{useYn, jdbcType=VARCHAR}
	AND T.FLD_YN = #{fldYn, jdbcType=VARCHAR}
	${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.pt.dao.PtCdBDao.insertPtCdB */ INSERT INTO PT_CD_B
CLS_CD, CD, CD_VA, RESC_ID, COMP_IDS, REF_VA1, REF_VA2, CD_DESC, SORT_ORDR, USE_YN, FLD_YN, SYS_CD_YN, REGR_UID, REG_DT, MODR_UID, MOD_DT,
#{clsCd, jdbcType=VARCHAR}, #{cd, jdbcType=VARCHAR}, #{cdVa, jdbcType=NVARCHAR}, #{rescId, jdbcType=VARCHAR}, #{compIds, jdbcType=VARCHAR}, #{refVa1, jdbcType=NVARCHAR}, #{refVa2, jdbcType=NVARCHAR}, #{cdDesc, jdbcType=NVARCHAR}, #{sortOrdr, jdbcType=INTEGER}, (SELECT IFNULL(MAX(P.SORT_ORDR),0)+1 FROM PT_CD_B P WHERE P.CLS_CD = #{clsCd, jdbcType=VARCHAR}), #{useYn, jdbcType=VARCHAR}, #{fldYn, jdbcType=VARCHAR}, #{sysCdYn, jdbcType=VARCHAR}, #{regrUid, jdbcType=VARCHAR},#{modrUid, jdbcType=VARCHAR}, NOW(),NOW(),NULL, STR_TO_DATE(#{regDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'), #{modrUid, jdbcType=VARCHAR}, NOW(),NULL, STR_TO_DATE(#{modDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'),

mapper.update:
/* com.innobiz.orange.web.pt.dao.PtCdBDao.updatePtCdB */ UPDATE PT_CD_B
CLS_CD = #{clsCd, jdbcType=VARCHAR}, CD = #{cd, jdbcType=VARCHAR}, CD_VA = #{cdVa, jdbcType=NVARCHAR}, RESC_ID = #{rescId, jdbcType=VARCHAR}, COMP_IDS = #{compIds, jdbcType=VARCHAR}, REF_VA1 = #{refVa1, jdbcType=NVARCHAR}, REF_VA2 = #{refVa2, jdbcType=NVARCHAR}, CD_DESC = #{cdDesc, jdbcType=NVARCHAR}, SORT_ORDR = #{sortOrdr, jdbcType=INTEGER}, USE_YN = #{useYn, jdbcType=VARCHAR}, FLD_YN = #{fldYn, jdbcType=VARCHAR}, SYS_CD_YN = #{sysCdYn, jdbcType=VARCHAR}, REGR_UID = #{regrUid, jdbcType=VARCHAR},
REG_DT = NOW(), REG_DT = NULL, REG_DT = STR_TO_DATE(#{regDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'),
MODR_UID = #{modrUid, jdbcType=VARCHAR},
MOD_DT = NOW(), MOD_DT = NULL, MOD_DT = STR_TO_DATE(#{modDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'),
AND CLS_CD = #{clsCd, jdbcType=VARCHAR} AND CD = #{cd, jdbcType=VARCHAR} ${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.pt.dao.PtCdBDao.deletePtCdB */
DELETE FROM PT_CD_B

	AND CLS_CD = #{clsCd, jdbcType=VARCHAR}
	AND CD = #{cd, jdbcType=VARCHAR}
	${whereSqllet}

mapper.select:
/* com.innobiz.orange.web.pt.dao.PtCdBDao.selectPtCdRescB */
SELECT
	R.RESC_ID,
	R.LANG_TYP_CD,
	R.RESC_VA
FROM PT_CD_B T, PT_RESC_B R
WHERE T.RESC_ID = R.RESC_ID
	 AND T.CLS_CD = #{clsCd, jdbcType=VARCHAR}
	 AND T.CD = #{cd, jdbcType=VARCHAR}
