mapper.select:
/* com.innobiz.orange.web.pt.dao.PtCompBDao.countPtCompB */
SELECT COUNT(*) CNT
FROM PT_COMP_B T

	AND T.COMP_ID = #{compId, jdbcType=VARCHAR}
	AND T.USE_YN = #{useYn, jdbcType=VARCHAR}
	AND T.DEL_YN = #{delYn, jdbcType=VARCHAR}
	${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.pt.dao.PtCompBDao.insertPtCompB */ INSERT INTO PT_COMP_B
COMP_ID, RCD, COMP_NM, RESC_ID, SORT_ORDR, USE_YN, DEL_YN, REGR_UID, REG_DT, MODR_UID, MOD_DT, MAIL_DOMAIN, LIC_CNT,
#{compId, jdbcType=VARCHAR}, #{rcd, jdbcType=VARCHAR}, #{compNm, jdbcType=NVARCHAR}, #{rescId, jdbcType=VARCHAR}, #{sortOrdr, jdbcType=INTEGER}, (SELECT IFNULL(MAX(P.SORT_ORDR),0)+1 FROM PT_COMP_B P), #{useYn, jdbcType=VARCHAR}, #{delYn, jdbcType=VARCHAR}, #{regrUid, jdbcType=VARCHAR},#{modrUid, jdbcType=VARCHAR}, NOW(),NOW(),NULL, STR_TO_DATE(#{regDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'), #{modrUid, jdbcType=VARCHAR}, NOW(),NULL, STR_TO_DATE(#{modDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'), #{mailDomain, jdbcType=VARCHAR}, NULL#{licCnt, jdbcType=INTEGER},

mapper.delete:
/* com.innobiz.orange.web.pt.dao.PtCompBDao.deletePtCompB */
DELETE FROM PT_COMP_B

	AND COMP_ID = #{compId, jdbcType=VARCHAR}
	${whereSqllet}
