mapper.select:
/* com.innobiz.orange.web.pt.dao.PtPushAppDDao.selectPtPushAppD */
SELECT

	T.CUST_CD,
	T.SEQ,
	T.CUST_NM,
	DATE_FORMAT(T.STRT_YMD, '%Y-%m-%d') STRT_YMD,
	DATE_FORMAT(T.END_YMD, '%Y-%m-%d') END_YMD,
	T.IPA_PATH,
	T.APK_PATH,
	T.DISP_NM,
	T.DESC_CONT,
	DATE_FORMAT(T.REG_DT, '%Y-%m-%d %H:%i:%s') REG_DT,
	T.USE_YN,

FROM PT_PUSH_APP_D T

	AND T.CUST_CD = #{custCd, jdbcType=VARCHAR}
	AND T.SEQ = #{seq, jdbcType=INTEGER}
	AND T.CUST_NM LIKE CONCAT('%', #{custNm, jdbcType=NVARCHAR}, '%')
	AND T.USE_YN = #{useYn, jdbcType=VARCHAR}
	${whereSqllet}

ORDER BY ${orderBy}
ORDER BY T.CUST_CD, T.SEQ

LIMIT #{pageStrt, jdbcType=NUMERIC}, #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.pt.dao.PtPushAppDDao.countPtPushAppD */
SELECT COUNT(*) CNT
FROM PT_PUSH_APP_D T

	AND T.CUST_CD = #{custCd, jdbcType=VARCHAR}
	AND T.SEQ = #{seq, jdbcType=INTEGER}
	AND T.CUST_NM LIKE CONCAT('%', #{custNm, jdbcType=NVARCHAR}, '%')
	AND T.USE_YN = #{useYn, jdbcType=VARCHAR}
	${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.pt.dao.PtPushAppDDao.insertPtPushAppD */ INSERT INTO PT_PUSH_APP_D
CUST_CD, SEQ, CUST_NM, STRT_YMD, END_YMD, IPA_PATH, APK_PATH, DISP_NM, DESC_CONT, REG_DT, USE_YN,
#{custCd, jdbcType=VARCHAR}, #{seq, jdbcType=INTEGER}, (SELECT IFNULL(MAX(P.SEQ),0)+1 FROM PT_PUSH_APP_D P WHERE P.CUST_CD = #{custCd, jdbcType=VARCHAR}), #{custNm, jdbcType=NVARCHAR}, NOW(),NULL, STR_TO_DATE(#{strtYmd, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'), NOW(),NULL, STR_TO_DATE(#{endYmd, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'), #{ipaPath, jdbcType=VARCHAR}, #{apkPath, jdbcType=VARCHAR}, #{dispNm, jdbcType=NVARCHAR}, #{descCont, jdbcType=NVARCHAR}, NOW(),NULL, STR_TO_DATE(#{regDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'), #{useYn, jdbcType=VARCHAR},

mapper.delete:
/* com.innobiz.orange.web.pt.dao.PtPushAppDDao.deletePtPushAppD */
DELETE FROM PT_PUSH_APP_D

	AND CUST_CD = #{custCd, jdbcType=VARCHAR}
	AND SEQ = #{seq, jdbcType=INTEGER}
	${whereSqllet}
