mapper.select:
/* com.innobiz.orange.web.pt.dao.PtSsoTDao.selectPtSsoT */
SELECT

	T.SSO_ID,
	T.USER_UID,
	T.LANG_TYP_CD,
	T.TGT_URL,
	T.AUTH_URL,
	DATE_FORMAT(T.REG_DT, '%Y-%m-%d %H:%i:%s') REG_DT,

FROM PT_SSO_T T

	AND T.SSO_ID = #{ssoId, jdbcType=VARCHAR}
	${whereSqllet}

ORDER BY ${orderBy}
ORDER BY T.SSO_ID

LIMIT #{pageStrt, jdbcType=NUMERIC}, #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.pt.dao.PtSsoTDao.countPtSsoT */
SELECT COUNT(*) CNT
FROM PT_SSO_T T

	AND T.SSO_ID = #{ssoId, jdbcType=VARCHAR}
	${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.pt.dao.PtSsoTDao.insertPtSsoT */ INSERT INTO PT_SSO_T
SSO_ID, USER_UID, LANG_TYP_CD, TGT_URL, AUTH_URL, REG_DT,
#{ssoId, jdbcType=VARCHAR}, #{userUid, jdbcType=VARCHAR}, #{langTypCd, jdbcType=VARCHAR}, #{tgtUrl, jdbcType=VARCHAR}, #{authUrl, jdbcType=VARCHAR}, NOW(),NULL, STR_TO_DATE(#{regDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'),

mapper.update:
/* com.innobiz.orange.web.pt.dao.PtSsoTDao.updatePtSsoT */ UPDATE PT_SSO_T
SSO_ID = #{ssoId, jdbcType=VARCHAR}, USER_UID = #{userUid, jdbcType=VARCHAR}, LANG_TYP_CD = #{langTypCd, jdbcType=VARCHAR}, TGT_URL = #{tgtUrl, jdbcType=VARCHAR}, AUTH_URL = #{authUrl, jdbcType=VARCHAR},
REG_DT = NOW(), REG_DT = NULL, REG_DT = STR_TO_DATE(#{regDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'),
AND SSO_ID = #{ssoId, jdbcType=VARCHAR} ${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.pt.dao.PtSsoTDao.deletePtSsoT */
DELETE FROM PT_SSO_T

	AND SSO_ID = #{ssoId, jdbcType=VARCHAR}
	${whereSqllet}
