mapper.select:
/* com.innobiz.orange.web.st.dao.StCatBDao.selectStCatB */ SELECT
T.CAT_ID, T.COMP_ID, (SELECT RESC_VA FROM ST_RESC_B WHERE RESC_ID = T.RESC_ID AND LANG_TYP_CD = IFNULL(#{queryLang, jdbcType=VARCHAR},'ko')) AS CAT_NM, T.RESC_ID, T.SORT_ORDR, T.REGR_UID, T.REG_DT, T.MODR_UID, T.MOD_DT,
FROM ST_CAT_B T
ORDER BY ${orderBy} ORDER BY T.SORT_ORDR
LIMIT #{pageStrt, jdbcType=NUMERIC}, #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.st.dao.StCatBDao.countStCatB */
SELECT COUNT(*) CNT
FROM ST_CAT_B T

mapper.insert:
/* com.innobiz.orange.web.st.dao.StCatBDao.insertStCatB */ INSERT INTO ST_CAT_B
CAT_ID, COMP_ID, CAT_NM, RESC_ID, SORT_ORDR, REGR_UID, REG_DT, MODR_UID, MOD_DT,
#{catId, jdbcType=VARCHAR}, #{compId, jdbcType=VARCHAR}, #{catNm, jdbcType=NVARCHAR}, #{rescId, jdbcType=VARCHAR}, #{sortOrdr, jdbcType=INTEGER}, #{regrUid, jdbcType=VARCHAR},NULL, NOW(),NULL, STR_TO_DATE(#{regDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'), #{modrUid, jdbcType=VARCHAR},NULL, NOW(),NULL, STR_TO_DATE(#{modDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'),

mapper.update:
/* com.innobiz.orange.web.st.dao.StCatBDao.updateStCatB */
UPDATE ST_CAT_B

	 CAT_NM = #{catNm, jdbcType=VARCHAR},
	 SORT_ORDR = #{sortOrdr, jdbcType=INTEGER},
	 MODR_UID = #{modrUid, jdbcType=VARCHAR},
	 
		 MOD_DT = NOW(),
		 MOD_DT = NULL,
		 MOD_DT = STR_TO_DATE(#{modDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'),
	


	 AND CAT_ID = #{catId, jdbcType=VARCHAR}
	 AND COMP_ID = #{compId, jdbcType=VARCHAR}

mapper.delete:
/* com.innobiz.orange.web.st.dao.StCatBDao.deleteStCatB */
DELETE FROM ST_CAT_B

	 AND CAT_ID = #{catId, jdbcType=VARCHAR}
	 AND COMP_ID = #{compId, jdbcType=VARCHAR}
