mapper.select:
/* com.innobiz.orange.web.wb.dao.WmRescBDao.selectWmRescB */
SELECT

	WR_T.COMP_ID , WR_T.RESC_ID,	WR_T.LANG_TYP_CD, WR_T.RESC_VA ,

FROM WM_RESC_B WR_T

ORDER BY ${orderBy}
ORDER BY WR_T.RESC_ID

LIMIT #{pageStrt, jdbcType=NUMERIC}, #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.wb.dao.WmRescBDao.countWmRescB */
SELECT COUNT(*) CNT
FROM WM_RESC_B WR_T

