COMP_ID , RESC_ID, LANG_TYP_CD, RESC_VA ,
#{compId, jdbcType=VARCHAR},#{rescId, jdbcType=VARCHAR},#{langTypCd, jdbcType=VARCHAR},#{rescVa, jdbcType=VARCHAR},

mapper.update:
/* com.innobiz.orange.web.wb.dao.WmRescBDao.updateWmRescB */
UPDATE WM_RESC_B

	 RESC_VA = #{rescVa, jdbcType=VARCHAR},


	 AND COMP_ID = #{compId, jdbcType=VARCHAR}
	 AND RESC_ID = #{rescId, jdbcType=VARCHAR}
	 AND LANG_TYP_CD = #{langTypCd, jdbcType=VARCHAR}

mapper.delete:
/* com.innobiz.orange.web.wb.dao.WmRescBDao.deleteWmRescB */
DELETE FROM WM_RESC_B

	 AND COMP_ID = #{compId, jdbcType=VARCHAR}
	 AND RESC_ID = #{rescId, jdbcType=VARCHAR}
	 AND LANG_TYP_CD = #{langTypCd, jdbcType=VARCHAR}
