mapper.select:
/* com.innobiz.orange.web.wc.dao.WcUserGrpBDao.selectWcUserGrpB */ SELECT
T.USER_GRP_ID, T.COMP_ID, (SELECT RESC_VA FROM WC_RESC_B WHERE RESC_ID = T.RESC_ID AND LANG_TYP_CD = IFNULL(#{queryLang, jdbcType=VARCHAR},'ko')) AS RESC_NM, T.RESC_ID, T.SORT_ORDR, T.MODR_UID, T.MOD_DT,
FROM WC_USER_GRP_B T
LEFT JOIN WC_RESC_B R ON T.RESC_ID = R.RESC_ID AND LANG_TYP_CD = IFNULL(#{queryLang, jdbcType=VARCHAR}, 'ko')
ORDER BY ${orderBy} ORDER BY T.USER_GRP_ID
LIMIT #{pageStrt, jdbcType=NUMERIC}, #{pageRowCnt, jdbcType=NUMERIC}

