mapper.select:
/* com.innobiz.orange.web.wf.dao.WfCdGrpBDao.selectWfCdGrpB */ SELECT
T.CD_GRP_ID, T.COMP_ID, T.CD_GRP_NM, T.RESC_ID, (SELECT RESC_VA FROM WF_RESC_B WHERE RESC_ID = T.RESC_ID AND LANG_TYP_CD = IFNULL(#{queryLang, jdbcType=VARCHAR},'ko')) AS CD_GRP_RESC_NM, T.CD_USE_YN, T.REGR_UID, T.REG_DT, T.MODR_UID, T.MOD_DT,
FROM WF_CD_GRP_B T INNER JOIN WF_RESC_B R ON R.RESC_ID = T.RESC_ID
ORDER BY ${orderBy} ORDER BY T.REG_DT
LIMIT #{pageStrt, jdbcType=NUMERIC}, #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.wf.dao.WfCdGrpBDao.countWfCdGrpB */
SELECT COUNT(*) CNT
FROM WF_CD_GRP_B T INNER JOIN WF_RESC_B R ON R.RESC_ID = T.RESC_ID

mapper.insert:
/* com.innobiz.orange.web.wf.dao.WfCdGrpBDao.insertWfCdGrpB */ INSERT INTO WF_CD_GRP_B
CD_GRP_ID, COMP_ID, CD_GRP_NM, RESC_ID, CD_USE_YN, REGR_UID, REG_DT, MODR_UID, MOD_DT,
#{cdGrpId, jdbcType=VARCHAR}, #{compId, jdbcType=VARCHAR}, #{cdGrpNm, jdbcType=VARCHAR}, #{rescId, jdbcType=VARCHAR}, #{cdUseYn, jdbcType=VARCHAR}, #{regrUid, jdbcType=VARCHAR},NULL, NOW(),NULL, STR_TO_DATE(#{regDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'), #{modrUid, jdbcType=VARCHAR},NULL, NOW(),NULL, STR_TO_DATE(#{modDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'),

mapper.update:
/* com.innobiz.orange.web.wf.dao.WfCdGrpBDao.updateWfCdGrpB */
UPDATE WF_CD_GRP_B

	 CD_GRP_NM = #{cdGrpNm, jdbcType=VARCHAR},
	 CD_USE_YN = #{cdUseYn, jdbcType=VARCHAR},
	 MODR_UID = #{modrUid, jdbcType=VARCHAR},
	 
		 MOD_DT = NOW(),
		 MOD_DT = NULL,
		 MOD_DT = STR_TO_DATE(#{modDt, jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%s'),
	


	 AND CD_GRP_ID = #{cdGrpId, jdbcType=VARCHAR}
	 AND COMP_ID = #{compId, jdbcType=VARCHAR}

mapper.delete:
/* com.innobiz.orange.web.wf.dao.WfCdGrpBDao.deleteWfCdGrpB */
DELETE FROM WF_CD_GRP_B

	 AND CD_GRP_ID = #{cdGrpId, jdbcType=VARCHAR}
	 AND COMP_ID = #{compId, jdbcType=VARCHAR}
