mapper.select:
/* com.innobiz.orange.web.wf.dao.WfFormGenBDao.selectWfFormGenB */ SELECT
T.GEN_ID, T.COMP_ID, T.STRT_DT, T.END_DT, T.PROC_STAT_CD, T.DEFRAG_YN, T.REGR_UID, T.REG_DT,
(SELECT RESC_VA FROM OR_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM OR_USER_B WHERE USER_UID = T.REGR_UID) AND LANG_TYP_CD = IFNULL(#{queryLang, jdbcType=VARCHAR},'ko')) REGR_NM,
FROM WF_FORM_GEN_B T
ORDER BY ${orderBy} ORDER BY T.REG_DT DESC
LIMIT #{pageStrt, jdbcType=NUMERIC}, #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.wf.dao.WfFormGenBDao.countWfFormGenB */
SELECT COUNT(*) CNT
FROM WF_FORM_GEN_B T

