	T.RESC_VA ,

FROM WF_RESC_B T

ORDER BY ${orderBy}
ORDER BY T.RESC_ID

LIMIT #{pageStrt, jdbcType=NUMERIC}, #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.wf.dao.WfRescBDao.countWfRescB */
SELECT COUNT(*) CNT
FROM WF_RESC_B T

