mapper.select:
/* com.innobiz.orange.web.wh.dao.WhReqEvalDDao.selectWhReqEvalD */ SELECT
T.REQ_NO, T.EVAL_NO, (SELECT RESC_VA FROM WH_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM WH_RES_EVAL_B WHERE EVAL_NO = T.EVAL_NO) AND LANG_TYP_CD = IFNULL(#{queryLang, jdbcType=VARCHAR}, 'ko')) EVAL_NM, T.EVAL_RSON, T.ADD_REQR, T.REGR_UID, T.REG_DT, (SELECT RESC_VA FROM OR_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM OR_USER_B WHERE USER_UID = T.REGR_UID) AND LANG_TYP_CD = IFNULL(#{queryLang, jdbcType=VARCHAR}, 'ko')) REGR_NM,
FROM WH_REQ_EVAL_D T
ORDER BY ${orderBy} ORDER BY T.REQ_NO
LIMIT #{pageStrt, jdbcType=NUMERIC}, #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.wh.dao.WhReqEvalDDao.countWhReqEvalD */
SELECT COUNT(*) CNT
FROM WH_REQ_EVAL_D T

