	T.RESC_VA ,

FROM WH_RESC_B T

ORDER BY ${orderBy}
ORDER BY T.RESC_ID

LIMIT #{pageStrt, jdbcType=NUMERIC}, #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.wh.dao.WhRescBDao.countWhRescB */
SELECT COUNT(*) CNT
FROM WH_RESC_B T

