mapper.select:
/* com.innobiz.orange.web.wl.dao.WlReprtGrpBDao.selectWlReprtGrpB */ SELECT
T.GRP_NO, T.COMP_ID, (SELECT RESC_VA FROM WL_RESC_B WHERE RESC_ID = T.RESC_ID AND LANG_TYP_CD = IFNULL(#{queryLang, jdbcType=VARCHAR},'ko') ) AS GRP_NM , T.RESC_ID, T.USE_YN, T.DFT_YN, (SELECT RESC_VA FROM OR_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM OR_USER_B WHERE USER_UID = T.REGR_UID) AND LANG_TYP_CD = IFNULL(#{queryLang, jdbcType=VARCHAR}, 'ko')) REGR_NM, T.REGR_UID, T.REG_DT, T.MODR_UID, T.MOD_DT,
FROM WL_REPRT_GRP_B T
ORDER BY ${orderBy} ORDER BY T.GRP_NM ASC
LIMIT #{pageStrt, jdbcType=NUMERIC}, #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.wl.dao.WlReprtGrpBDao.countWlReprtGrpB */
SELECT COUNT(*) CNT
FROM WL_REPRT_GRP_B T

