mapper.select:
/* com.innobiz.orange.web.wv.dao.WvSurvReplyDDao.selectWvSurvReplyD */

SELECT


	WSRD_T.QUES_ID, WSRD_T.COMP_ID, WSRD_T.SURV_ID, WSRD_T.REPLY_NO,
	WSRD_T.REPLYR_UID, WSRD_T.REPLYR_DEPT_ID, WSRD_T.REPLY_DT, 
	WSRD_T.MULC_INPUT_REPLY_CONT, WSRD_T.OEND_REPLY_CONT, 
	
	

FROM WV_SURV_REPLY_D WSRD_T


ORDER BY ${orderBy}
	

LIMIT #{pageStrt, jdbcType=NUMERIC}, #{pageRowCnt, jdbcType=NUMERIC}

/* com.innobiz.orange.web.wv.dao.WvSurvReplyDDao.countWvSurvReplyD */
SELECT COUNT(*) CNT
FROM WV_SURV_REPLY_D WSRD_T

