mapper.select:
/* com.innobiz.orange.web.ap.dao.ApAgnApntRDao.countApAgnApntR */
SELECT COUNT(*) CNT
FROM AP_AGN_APNT_R T

	AND T.USER_UID = #{userUid, jdbcType=VARCHAR}
	AND T.AGNT_UID = #{agntUid, jdbcType=VARCHAR}
	${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.ap.dao.ApAgnApntRDao.insertApAgnApntR */ INSERT INTO AP_AGN_APNT_R
USER_UID, AGNT_UID, AGNT_RESC_ID, SORT_ORDR,
#{userUid, jdbcType=VARCHAR}, #{agntUid, jdbcType=VARCHAR}, #{agntRescId, jdbcType=VARCHAR}, #{sortOrdr, jdbcType=INTEGER}, (SELECT NVL(MAX(SORT_ORDR),0)+1 FROM AP_AGN_APNT_R),

mapper.update:
/* com.innobiz.orange.web.ap.dao.ApAgnApntRDao.updateApAgnApntR */
UPDATE AP_AGN_APNT_R

	USER_UID = #{userUid, jdbcType=VARCHAR},
	AGNT_UID = #{agntUid, jdbcType=VARCHAR},
	AGNT_RESC_ID = #{agntRescId, jdbcType=VARCHAR},
	SORT_ORDR = #{sortOrdr, jdbcType=INTEGER},


	AND USER_UID = #{userUid, jdbcType=VARCHAR}
	AND AGNT_UID = #{agntUid, jdbcType=VARCHAR}
	${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.ap.dao.ApAgnApntRDao.deleteApAgnApntR */
DELETE FROM AP_AGN_APNT_R

	AND USER_UID = #{userUid, jdbcType=VARCHAR}
	AND AGNT_UID = #{agntUid, jdbcType=VARCHAR}
	${whereSqllet}
