mapper.select:
/* com.innobiz.orange.web.ap.dao.ApErpIntgFileDDao.selectApErpIntgFileD */

SELECT * FROM (

SELECT

	
	* FROM (SELECT N.*, ROWNUM RNUM FROM (SELECT
	
	T.INTG_NO,
	T.SEQ,
	T.ATT_DISP_NM,
	T.FILE_EXT,
	T.FILE_KB,
	T.FILE_CONT,

FROM AP_ERP_INTG_FILE_D T

	AND T.INTG_NO = #{intgNo, jdbcType=BIGINT}
	AND T.SEQ = #{seq, jdbcType=INTEGER}
	${whereSqllet}

ORDER BY ${orderBy}
ORDER BY T.INTG_NO, T.SEQ

) N WHERE ROWNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} ) M

/* com.innobiz.orange.web.ap.dao.ApErpIntgFileDDao.countApErpIntgFileD */
SELECT COUNT(*) CNT
FROM AP_ERP_INTG_FILE_D T

	AND T.INTG_NO = #{intgNo, jdbcType=BIGINT}
	AND T.SEQ = #{seq, jdbcType=INTEGER}
	${whereSqllet}

