/* com.innobiz.orange.web.ap.dao.ApOngdExDDao.selectApOngdExD */

SELECT * FROM (

SELECT

	
	* FROM (SELECT N.*, ROWNUM RNUM FROM (SELECT
	
	T.APV_NO,
	T.EX_ID,
	T.EX_VA,

FROM AP_${storage}_EX_D T

	AND T.APV_NO = #{apvNo, jdbcType=BIGINT}
	AND T.EX_ID = #{exId, jdbcType=VARCHAR}
	AND T.APV_NO IN ( #{item, jdbcType=VARCHAR} )
	${whereSqllet}

ORDER BY ${orderBy}
ORDER BY T.APV_NO, T.EX_ID

) N WHERE ROWNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} ) M

/* com.innobiz.orange.web.ap.dao.ApOngdExDDao.countApOngdExD */
SELECT COUNT(*) CNT
FROM AP_${storage}_EX_D T

	AND T.APV_NO = #{apvNo, jdbcType=BIGINT}
	AND T.EX_ID = #{exId, jdbcType=VARCHAR}
	AND T.APV_NO IN ( #{item, jdbcType=VARCHAR} )
	${whereSqllet}

