mapper.select:
/* com.innobiz.orange.web.ap.dao.ApOngdPichDDao.selectApOngdPichD */

SELECT * FROM (

SELECT

	
	* FROM (SELECT N.*, ROWNUM RNUM FROM (SELECT
	
	T.APV_NO,
	T.PICH_TYP_CD,
	T.PICH_UID,
	T.PICH_DEPT_ID,
	T.PICH_NM,
	T.PICH_DEPT_NM,
	T.PICH_OPIN_CONT,
	T.HDL_STAT_CD,
	TO_CHAR(T.HDL_DT, 'YYYY-MM-DD HH24:MI:SS') HDL_DT,
	TO_CHAR(T.VW_DT, 'YYYY-MM-DD HH24:MI:SS') VW_DT,
	TO_CHAR(T.PREV_APVR_APV_DT, 'YYYY-MM-DD HH24:MI:SS') PREV_APVR_APV_DT,
	(SELECT RESC_VA FROM PT_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM PT_CD_B WHERE CLS_CD = 'HDL_STAT_CD' AND CD = T.HDL_STAT_CD) AND LANG_TYP_CD = NVL(#{queryLang, jdbcType=VARCHAR},'ko')) HDL_STAT_NM,

FROM AP_${storage}_PICH_D T

	AND T.APV_NO = #{apvNo, jdbcType=BIGINT}
	AND T.PICH_TYP_CD = #{pichTypCd, jdbcType=VARCHAR}
	AND T.PICH_UID = #{pichUid, jdbcType=VARCHAR}
	AND T.PICH_DEPT_ID = #{pichDeptId, jdbcType=VARCHAR}
	${whereSqllet}

ORDER BY ${orderBy}
ORDER BY T.APV_NO, T.PICH_TYP_CD

) N WHERE ROWNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} ) M

/* com.innobiz.orange.web.ap.dao.ApOngdPichDDao.countApOngdPichD */
SELECT COUNT(*) CNT
FROM AP_${storage}_PICH_D T

	AND T.APV_NO = #{apvNo, jdbcType=BIGINT}
	AND T.PICH_TYP_CD = #{pichTypCd, jdbcType=VARCHAR}
	AND T.PICH_UID = #{pichUid, jdbcType=VARCHAR}
	AND T.PICH_DEPT_ID = #{pichDeptId, jdbcType=VARCHAR}
	${whereSqllet}

mapper.insert:
/* com.innobiz.orange.web.ap.dao.ApOngdPichDDao.insertApOngdPichD */ INSERT INTO AP_${storage}_PICH_D
APV_NO, PICH_TYP_CD, PICH_UID, PICH_DEPT_ID, PICH_NM, PICH_DEPT_NM, PICH_OPIN_CONT, HDL_STAT_CD, HDL_DT, VW_DT, PREV_APVR_APV_DT,
#{apvNo, jdbcType=BIGINT}, #{pichTypCd, jdbcType=VARCHAR}, #{pichUid, jdbcType=VARCHAR}, #{pichDeptId, jdbcType=VARCHAR}, #{pichNm, jdbcType=NVARCHAR}, #{pichDeptNm, jdbcType=NVARCHAR}, #{pichOpinCont, jdbcType=NVARCHAR}, #{hdlStatCd, jdbcType=VARCHAR}, SYSDATE,NULL, TO_DATE(#{hdlDt, jdbcType=VARCHAR}, 'YYYY-MM-DD HH24:MI:SS'), SYSDATE,NULL, TO_DATE(#{vwDt, jdbcType=VARCHAR}, 'YYYY-MM-DD HH24:MI:SS'), SYSDATE,NULL, TO_DATE(#{prevApvrApvDt, jdbcType=VARCHAR}, 'YYYY-MM-DD HH24:MI:SS'),

mapper.update:
/* com.innobiz.orange.web.ap.dao.ApOngdPichDDao.updateApOngdPichD */ UPDATE AP_${storage}_PICH_D
APV_NO = #{apvNo, jdbcType=BIGINT}, PICH_TYP_CD = #{pichTypCd, jdbcType=VARCHAR}, PICH_UID = #{pichUid, jdbcType=VARCHAR}, PICH_DEPT_ID = #{pichDeptId, jdbcType=VARCHAR}, PICH_NM = #{pichNm, jdbcType=NVARCHAR}, PICH_DEPT_NM = #{pichDeptNm, jdbcType=NVARCHAR}, PICH_OPIN_CONT = #{pichOpinCont, jdbcType=NVARCHAR}, HDL_STAT_CD = #{hdlStatCd, jdbcType=VARCHAR},
HDL_DT = SYSDATE, HDL_DT = NULL, HDL_DT = TO_DATE(#{hdlDt, jdbcType=VARCHAR}, 'YYYY-MM-DD HH24:MI:SS'),
VW_DT = SYSDATE, VW_DT = NULL, VW_DT = TO_DATE(#{vwDt, jdbcType=VARCHAR}, 'YYYY-MM-DD HH24:MI:SS'),
PREV_APVR_APV_DT = SYSDATE, PREV_APVR_APV_DT = NULL, PREV_APVR_APV_DT = TO_DATE(#{prevApvrApvDt, jdbcType=VARCHAR}, 'YYYY-MM-DD HH24:MI:SS'),
AND APV_NO = #{apvNo, jdbcType=BIGINT} AND PICH_TYP_CD = #{pichTypCd, jdbcType=VARCHAR} ${whereSqllet}

mapper.delete:
/* com.innobiz.orange.web.ap.dao.ApOngdPichDDao.deleteApOngdPichD */
DELETE FROM AP_${storage}_PICH_D

	AND APV_NO = #{apvNo, jdbcType=BIGINT}
