/* com.innobiz.orange.web.bb.dao.BaColmDispDDao.selectBaColmDispD */
SELECT * FROM (
SELECT
* FROM (SELECT N.*, ROWNUM RNUM FROM (SELECT
T.BRD_ID, T.COLM_ID, T.USE_YN, T.REG_DISP_YN, T.MOD_DISP_YN, T.READ_DISP_YN, T.LIST_DISP_YN, T.LIST_DISP_ORDR, T.WDTH_PERC, T.ALN_VA, T.SORT_OPT_VA, T.DATA_SORT_VA, T.READ_DISP_ORDR, T.COLS_WDTH_VA, T.TITL_COL_YN, T.MANDATORY_YN, T.REGR_UID, T.REG_DT, T.MODR_UID, T.MOD_DT,
FROM BA_COLM_DISP_D T
AND T.BRD_ID = #{brdId, jdbcType=VARCHAR} AND T.COLM_ID = #{colmId, jdbcType=VARCHAR} AND T.USE_YN = #{useYn, jdbcType=VARCHAR} AND T.LIST_DISP_YN = #{listDispYn, jdbcType=VARCHAR} AND T.READ_DISP_YN = #{readDispYn, jdbcType=VARCHAR} ${whereSqllet}
ORDER BY ${orderBy} ORDER BY T.COLM_ID ASC
) N WHERE ROWNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} ) M

/* com.innobiz.orange.web.bb.dao.BaColmDispDDao.countBaColmDispD */
SELECT COUNT(*) CNT
FROM BA_COLM_DISP_D T

	 AND T.BRD_ID = #{brdId, jdbcType=VARCHAR}
	 AND T.COLM_ID = #{colmId, jdbcType=VARCHAR}
	 AND T.USE_YN = #{useYn, jdbcType=VARCHAR}
	 AND T.LIST_DISP_YN = #{listDispYn, jdbcType=VARCHAR}
	 AND T.READ_DISP_YN = #{readDispYn, jdbcType=VARCHAR}
	 ${whereSqllet}

