	T.SORT_ORDR,

FROM BA_PLT_SETUP_D T

	AND T.USER_UID = #{userUid, jdbcType=VARCHAR}
	AND T.PLT_ID = #{pltId, jdbcType=VARCHAR}
	AND T.BX_ID = #{bxId, jdbcType=VARCHAR}
	${whereSqllet}

ORDER BY ${orderBy}
ORDER BY T.USER_UID, T.BX_ID

) N WHERE ROWNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} ) M

