mapper.select:
/* com.innobiz.orange.web.bb.dao.BaTmpSaveLDao.selectBaTmpSaveL */
SELECT * FROM (
SELECT
* FROM (SELECT N.*, ROWNUM RNUM FROM (SELECT
T.BULL_ID, T.BRD_ID, (SELECT RESC_VA FROM BA_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM BA_BRD_B WHERE BRD_ID = T.BRD_ID) AND LANG_TYP_CD = NVL(#{queryLang, jdbcType=VARCHAR},'ko')) BRD_NM, T.SUBJ, T.CONT, T.BRD_ID_LIST, T.REGR_UID, (SELECT RESC_VA FROM OR_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM OR_USER_B WHERE USER_UID = T.REGR_UID) AND LANG_TYP_CD = NVL(#{queryLang, jdbcType=VARCHAR}, 'ko')) REGR_NM, T.REG_DT, T.MODR_UID, (SELECT RESC_VA FROM OR_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM OR_USER_B WHERE USER_UID = T.MODR_UID) AND LANG_TYP_CD = NVL(#{queryLang, jdbcType=VARCHAR}, 'ko')) MODR_NM, T.MOD_DT, --(SELECT COUNT(*) FROM BA_BULL_FILE_D WHERE REF_ID = TO_CHAR(T.BULL_ID)) FILE_CNT, NVL(O.SECU_YN, 'N') SECU_YN, NVL(O.UGNT_YN, 'N') UGNT_YN, NVL(O.NOTC_YN, 'N') NOTC_YN,
FROM BA_TMP_SAVE_L T LEFT JOIN BA_BULL_OPT_D O ON T.BULL_ID = O.BULL_ID
LEFT JOIN BA_BRD_B B ON T.BRD_ID = B.BRD_ID LEFT JOIN BA_RESC_B R ON B.RESC_ID = R.RESC_ID AND LANG_TYP_CD = NVL(#{queryLang, jdbcType=VARCHAR}, 'ko')
AND T.REGR_UID = #{regrUid, jdbcType=VARCHAR} AND T.BULL_ID = #{bullId, jdbcType=INTEGER} AND T.BRD_ID = #{brdId, jdbcType=VARCHAR} AND T.SUBJ like '%' || #{schWord, jdbcType=VARCHAR} || '%' AND T.CONT like '%' || #{schWord, jdbcType=VARCHAR} || '%' AND R.RESC_VA like '%' || #{schWord, jdbcType=VARCHAR} || '%' ${whereSqllet}
ORDER BY ${orderBy} ORDER BY T.BULL_ID DESC
) N WHERE ROWNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} ) M

/* com.innobiz.orange.web.bb.dao.BaTmpSaveLDao.countBaTmpSaveL */
SELECT COUNT(*) CNT
FROM BA_TMP_SAVE_L T

	LEFT JOIN BA_BRD_B B ON T.BRD_ID = B.BRD_ID
	LEFT JOIN BA_RESC_B R ON B.RESC_ID = R.RESC_ID AND LANG_TYP_CD = NVL(#{queryLang, jdbcType=VARCHAR}, 'ko')


	AND T.REGR_UID = #{regrUid, jdbcType=VARCHAR}
	 AND T.BULL_ID = #{bullId, jdbcType=INTEGER}
	 AND T.BRD_ID = #{brdId, jdbcType=VARCHAR}
	 AND T.SUBJ like '%' || #{schWord, jdbcType=VARCHAR} || '%'
	 AND T.CONT like '%' || #{schWord, jdbcType=VARCHAR} || '%'
	 AND R.RESC_VA like '%' || #{schWord, jdbcType=VARCHAR} || '%'
	 ${whereSqllet}

