BULL_ID, BRD_ID, SUBJ, CONT, BRD_ID_LIST, REGR_UID, REG_DT, MODR_UID, MOD_DT,
#{bullId, jdbcType=VARCHAR}, #{brdId, jdbcType=VARCHAR}, #{subj, jdbcType=NVARCHAR}, #{cont, jdbcType=NCLOB}, #{brdIdList, jdbcType=VARCHAR}, #{regrUid, jdbcType=VARCHAR},#{modrUid, jdbcType=VARCHAR}, SYSDATE,SYSDATE,NULL, TO_DATE(#{regDt, jdbcType=VARCHAR}, 'YYYY-MM-DD HH24:MI:SS'), #{modrUid, jdbcType=VARCHAR}, SYSDATE,NULL, TO_DATE(#{modDt, jdbcType=VARCHAR}, 'YYYY-MM-DD HH24:MI:SS'),

