mapper.select:
/* com.innobiz.orange.web.ct.dao.CtSchdlCatClsBDao.selectCtSchdlCatClsB */

SELECT * FROM (

SELECT

	
	* FROM (SELECT N.*, ROWNUM RNUM FROM (SELECT
	
	T.CAT_ID ,T.COMP_ID , T.SORT_ORDR , T.RESC_ID , T.BGCOL_CD , T.FONT_COLR_CD , T.USE_YN , TO_CHAR(T.REG_DT, 'YYYY-MM-DD HH24:MI:SS') REG_DT, 
    (SELECT RESC_VA FROM CT_RESC_B WHERE RESC_ID = T.RESC_ID AND LANG_TYP_CD = NVL(#{queryLang, jdbcType=VARCHAR},'ko') ) AS CAT_NM ,

FROM CT_SCHDL_CAT_CLS_B T 

ORDER BY ${orderBy}
ORDER BY T.SORT_ORDR ASC 

) N WHERE ROWNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} ) M

/* com.innobiz.orange.web.ct.dao.CtSchdlCatClsBDao.countCtSchdlCatClsB */
SELECT COUNT(*) CNT
FROM CT_SCHDL_CAT_CLS_B T

mapper.insert:
/* com.innobiz.orange.web.ct.dao.CtSchdlCatClsBDao.insertCtSchdlCatClsB */ INSERT INTO CT_SCHDL_CAT_CLS_B
CAT_ID,COMP_ID,SORT_ORDR,RESC_ID,BGCOL_CD,FONT_COLR_CD,USE_YN,CAT_NM,REGR_UID,REG_DT,
#{catId, jdbcType=VARCHAR},#{compId, jdbcType=VARCHAR},#{sortOrdr, jdbcType=VARCHAR},#{rescId, jdbcType=VARCHAR},#{bgcolCd, jdbcType=VARCHAR}, #{fontColrCd, jdbcType=VARCHAR}, #{useYn, jdbcType=VARCHAR},#{catNm, jdbcType=NVARCHAR},#{regrUid, jdbcType=VARCHAR}, SYSDATE,SYSDATE,NULL,

mapper.update:
/* com.innobiz.orange.web.ct.dao.CtSchdlCatClsBDao.updateCtSchdlCatClsB */ UPDATE CT_SCHDL_CAT_CLS_B
SORT_ORDR = #{sortOrdr, jdbcType=VARCHAR}, BGCOL_CD = #{bgcolCd, jdbcType=VARCHAR}, FONT_COLR_CD = #{fontColrCd, jdbcType=VARCHAR}, USE_YN = #{useYn, jdbcType=VARCHAR}, CAT_NM = #{catNm, jdbcType=NVARCHAR}, MODR_UID = #{modrUid, jdbcType=VARCHAR},
MOD_DT = SYSDATE, MOD_DT = NULL, MOD_DT = TO_DATE(#{modDt, jdbcType=VARCHAR}, 'YYYY-MM-DD HH24:MI:SS'),
AND CAT_ID = #{catId, jdbcType=VARCHAR} AND COMP_ID = #{compId, jdbcType=VARCHAR}

mapper.delete:
/* com.innobiz.orange.web.ct.dao.CtSchdlCatClsBDao.deleteCtSchdlCatClsB */
DELETE FROM CT_SCHDL_CAT_CLS_B

	 AND CAT_ID = #{catId, jdbcType=VARCHAR}
	 AND COMP_ID = #{compId, jdbcType=VARCHAR}
	 AND USE_YN = #{useYn, jdbcType=VARCHAR}
