mapper.select:
/* com.innobiz.orange.web.ct.dao.CtBullMastBDao.selectCtBullMastB */
SELECT * FROM (
SELECT
* FROM (SELECT N.*, ROWNUM RNUM FROM (SELECT
CBMB_T.BULL_ID, CBMB_T.COMP_ID, CBMB_T.CT_ID, CBMB_T.CT_FNC_ID, CBMB_T.BULL_PID, CBMB_T.CT_FNC_ORDR, CBMB_T.REPLY_GRP_ID, CBMB_T.REPLY_ORDR, CBMB_T.REPLY_DPTH, CBMB_T.BULL_STAT_CD, CBMB_T.BULL_REZV_YN, CBMB_T.TGT_DEPT_YN, CBMB_T.TGT_USER_YN, CBMB_T.SUBJ, CBMB_T.BULL_REZV_DT, CBMB_T.BULL_EXPR_DT, CBMB_T.CONT, CBMB_T.READ_CNT, CBMB_T.PROS_CNT, CBMB_T.CONS_CNT, CBMB_T.RECMD_CNT, CBMB_T.REGR_UID, CBMB_T.REG_DT, CBMB_T.MODR_UID, CBMB_T.MOD_DT, CBMB_T.CT_FNC_LOC_STEP, CBMB_T.CT_FNC_UID, CBMB_T.CT_FNC_PID, CBMB_T.SCRE,
(SELECT RESC_VA FROM OR_RESC_B WHERE RESC_ID = ( SELECT RESC_ID FROM OR_USER_B WHERE USER_UID = ( SELECT MAST_UID FROM CT_ESTB_B WHERE CBMB_T.CT_ID = CT_ID
) ) AND LANG_TYP_CD = #{queryLang, jdbcType=VARCHAR} )MAST_NM, ( SELECT MAST_UID FROM CT_ESTB_B WHERE CBMB_T.CT_ID = CT_ID )MAST_UID, (SELECT RESC_VA FROM OR_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM OR_USER_B WHERE USER_UID = CBMB_T.REGR_UID) AND LANG_TYP_CD = NVL(#{queryLang, jdbcType=VARCHAR}, 'ko')) REGR_NM, (SELECT RESC_VA FROM OR_RESC_B WHERE RESC_ID = (SELECT RESC_ID FROM OR_USER_B WHERE USER_UID = CBMB_T.MODR_UID) AND LANG_TYP_CD = NVL(#{queryLang, jdbcType=VARCHAR}, 'ko')) MODR_NM, ( SELECT CASE WHEN COUNT(1) > 0 THEN 'Y' ELSE 'N' END ATT_YN FROM CT_FILE_D WHERE REF_ID=TO_CHAR(CBMB_T.BULL_ID)
) AS ATT_YN, ( SELECT RESC_VA FROM CT_RESC_B WHERE RESC_ID = (SELECT CT_SUBJ_RESC FROM CT_ESTB_B WHERE CT_ID = CBMB_T.CT_ID) AND LANG_TYP_CD = #{langTyp, jdbcType=VARCHAR} ) CT_NM, CASE WHEN CBMB_T.REG_DT > SYSDATE -1 THEN 'Y' ELSE 'N' END NEW_YN, (SELECT COUNT(*) FROM CT_BULL_MAST_CMD_D WHERE BULL_ID = CBMB_T.BULL_ID) CMT_CNT,
( SELECT RESC_VA FROM CT_RESC_B WHERE LANG_TYP_CD = #{langTyp, jdbcType=VARCHAR} AND RESC_ID=( SELECT CT_FNC_SUBJ_RESC_ID FROM CT_FNC_D WHERE CT_FNC_UID = CBMB_T.CT_FNC_UID ) )FNC_NM, ( SELECT URL FROM CT_FNC_MNG_B WHERE CBMB_T.CT_FNC_ID = CT_FNC_ID
)CT_FNC_URL, ( SELECT REL_TBL_SUBJ FROM CT_FNC_MNG_B WHERE CBMB_T.CT_FNC_ID = CT_FNC_ID )REL_TBL_SUBJ,
FROM CT_BULL_MAST_B CBMB_T
LEFT JOIN OR_USER_B U ON CBMB_T.REGR_UID = U.USER_UID LEFT JOIN OR_RESC_B R ON U.RESC_ID = R.RESC_ID AND LANG_TYP_CD = NVL(#{queryLang, jdbcType=VARCHAR}, 'ko')
ORDER BY ${orderBy} ORDER BY CBMB_T.REPLY_GRP_ID DESC, CBMB_T.REPLY_ORDR ASC
) N WHERE ROWNUM <= #{pageNo, jdbcType=NUMERIC} * #{pageRowCnt, jdbcType=NUMERIC}) WHERE RNUM > (#{pageNo, jdbcType=NUMERIC} - 1) * #{pageRowCnt, jdbcType=NUMERIC} ) M

/* com.innobiz.orange.web.ct.dao.CtBullMastBDao.selectMinReplyOrdr */
SELECT MIN(REPLY_ORDR) REPLY_ORDR
FROM CT_BULL_MAST_B CBMB_T

	AND CBMB_T.REPLY_GRP_ID = #{replyGrpId, jdbcType=NUMERIC}
	AND CBMB_T.REPLY_ORDR > #{replyOrdr, jdbcType=NUMERIC}
	AND CBMB_T.REPLY_DPTH <= #{replyDpth, jdbcType=NUMERIC}

/* com.innobiz.orange.web.ct.dao.CtBullMastBDao.selectMaxReplyOrdr */
SELECT MAX(REPLY_ORDR) REPLY_ORDR
FROM CT_BULL_MAST_B CBMB_T

	AND CBMB_T.REPLY_GRP_ID = #{replyGrpId, jdbcType=NUMERIC}

/* com.innobiz.orange.web.ct.dao.CtBullMastBDao.selectNextId */
SELECT

	MAX(BULL_ID) AS BULL_ID,

FROM CT_BULL_MAST_B CBMB_T

	LEFT JOIN OR_USER_B U ON CBMB_T.REGR_UID = U.USER_UID
	LEFT JOIN OR_RESC_B R ON U.RESC_ID = R.RESC_ID AND LANG_TYP_CD = NVL(#{queryLang, jdbcType=VARCHAR}, 'ko')


	AND BULL_ID < #{bullId, jdbcType=INTEGER}
	 AND REPLY_DPTH = 0
	
	 ${whereSqllet}

/* com.innobiz.orange.web.ct.dao.CtBullMastBDao.selectPrevId */
SELECT

	MIN(BULL_ID) AS BULL_ID,

FROM CT_BULL_MAST_B CBMB_T

	LEFT JOIN OR_USER_B U ON CBMB_T.REGR_UID = U.USER_UID
	LEFT JOIN OR_RESC_B R ON U.RESC_ID = R.RESC_ID AND LANG_TYP_CD = NVL(#{queryLang, jdbcType=VARCHAR}, 'ko')


	AND BULL_ID > #{bullId, jdbcType=NUMERIC}
	 AND REPLY_DPTH = 0
	
	 ${whereSqllet}

mapper.update:
/* com.innobiz.orange.web.ct.dao.CtBullMastBDao.updateReplyOrdr */
UPDATE CT_BULL_MAST_B

	REPLY_ORDR = REPLY_ORDR + 1,


	AND REPLY_GRP_ID = #{replyGrpId, jdbcType=NUMERIC}
	AND REPLY_ORDR >= #{replyOrdr, jdbcType=INTEGER}

